import{_ as p,c as e,o as a,a as t}from"./app.256e99ba.js";const f='{"title":"\u8FD1\u671F\u7BA1\u7406\u7684\u8BA4\u77E5\u8FED\u4EE3","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u6BD4\u4E2A\u4EBA\u76EE\u6807\u66F4\u91CD\u8981\u7684\u662F\u56E2\u961F\u76EE\u6807","slug":"\u6BD4\u4E2A\u4EBA\u76EE\u6807\u66F4\u91CD\u8981\u7684\u662F\u56E2\u961F\u76EE\u6807"},{"level":2,"title":"\u7EC4\u7EC7\u5229\u76CA\u662F\u7BA1\u7406\u8005\u51B3\u7B56\u7684\u51FA\u53D1\u70B9","slug":"\u7EC4\u7EC7\u5229\u76CA\u662F\u7BA1\u7406\u8005\u51B3\u7B56\u7684\u51FA\u53D1\u70B9"},{"level":2,"title":"\u7BA1\u7406\u5C31\u662F\u628A\u4EBA\u548C\u4E8B\u505A\u5230\u5145\u5206\u7ED3\u5408","slug":"\u7BA1\u7406\u5C31\u662F\u628A\u4EBA\u548C\u4E8B\u505A\u5230\u5145\u5206\u7ED3\u5408"},{"level":2,"title":"\u964D\u4F4E\u4E0B\u5C5E\u7684\u5DE5\u4F5C\u96BE\u5EA6\u5E76\u6FC0\u53D1\u5176\u79EF\u6781\u6027","slug":"\u964D\u4F4E\u4E0B\u5C5E\u7684\u5DE5\u4F5C\u96BE\u5EA6\u5E76\u6FC0\u53D1\u5176\u79EF\u6781\u6027"},{"level":2,"title":"\u4E3A\u5927\u5BB6\u6811\u7ACB\u4E00\u4E2A\u597D\u7684\u699C\u6837\u6216\u5408\u7406\u7684\u76EE\u6807","slug":"\u4E3A\u5927\u5BB6\u6811\u7ACB\u4E00\u4E2A\u597D\u7684\u699C\u6837\u6216\u5408\u7406\u7684\u76EE\u6807"}],"relativePath":"manage/sum.md","lastUpdated":1681349490001}',r={},d=t('<h1 id="\u8FD1\u671F\u7BA1\u7406\u7684\u8BA4\u77E5\u8FED\u4EE3" tabindex="-1">\u8FD1\u671F\u7BA1\u7406\u7684\u8BA4\u77E5\u8FED\u4EE3 <a class="header-anchor" href="#\u8FD1\u671F\u7BA1\u7406\u7684\u8BA4\u77E5\u8FED\u4EE3" aria-hidden="true">#</a></h1><p>\u7BA1\u7406\u9996\u5148\u662F\u7BA1\u4E8B\uFF0C\u56E0\u4E3A\u4EBA\u662F\u7BA1\u4E0D\u4F4F\u7684\u3002</p><h2 id="\u6BD4\u4E2A\u4EBA\u76EE\u6807\u66F4\u91CD\u8981\u7684\u662F\u56E2\u961F\u76EE\u6807" tabindex="-1">\u6BD4\u4E2A\u4EBA\u76EE\u6807\u66F4\u91CD\u8981\u7684\u662F\u56E2\u961F\u76EE\u6807 <a class="header-anchor" href="#\u6BD4\u4E2A\u4EBA\u76EE\u6807\u66F4\u91CD\u8981\u7684\u662F\u56E2\u961F\u76EE\u6807" aria-hidden="true">#</a></h2><p>\u7BA1\u7406\u8005\u8981\u6709\u5927\u5C40\u773C\u5149\uFF0C\u8981\u4ECE\u7EC4\u7EC7\u7684\u89D2\u5EA6\uFF0C\u53BB\u5BF9\u5F85\u5DE5\u4F5C\uFF0C\u53BB\u5236\u5B9A\u51B3\u7B56\uFF0C\u505A\u4E8B\u548C\u601D\u8003\u95EE\u9898\u7684\u51FA\u53D1\u70B9\uFF0C\u9996\u5148\u662F\u56E2\u961F\u5229\u76CA\u548C\u56E2\u961F\u76EE\u6807\u3002</p><p>\u6BCF\u4E2A\u540C\u4E8B\u90FD\u8BF4\u72EC\u4E00\u65E0\u4E8C\u7684\uFF0C\u9762\u5BF9\u8FD9\u4E9B\u9C9C\u6D3B\u7684\u4E2A\u4F53\u65F6\uFF0C\u8981\u5C3D\u91CF\u907F\u514D\u611F\u60C5\u7528\u4E8B\u6216\u8005\u6015\u9EBB\u70E6\u800C\u4E00\u518D\u8BA9\u6B65\uFF0C\u4ECE\u800C\u505A\u51FA\u8FDD\u80CC\u56E2\u961F\u76EE\u6807\u548C\u5229\u76CA\u7684\u51B3\u7B56\u3002</p><p>\u8F9E\u9000\u4E00\u540D\u4E0D\u5408\u9002\u7684\u5458\u5DE5\uFF0C\u662F\u4E00\u540D\u7BA1\u7406\u8005\u8D70\u5411\u6210\u719F\u7684\u7B2C\u4E00\u8BFE\u3002</p><h2 id="\u7EC4\u7EC7\u5229\u76CA\u662F\u7BA1\u7406\u8005\u51B3\u7B56\u7684\u51FA\u53D1\u70B9" tabindex="-1">\u7EC4\u7EC7\u5229\u76CA\u662F\u7BA1\u7406\u8005\u51B3\u7B56\u7684\u51FA\u53D1\u70B9 <a class="header-anchor" href="#\u7EC4\u7EC7\u5229\u76CA\u662F\u7BA1\u7406\u8005\u51B3\u7B56\u7684\u51FA\u53D1\u70B9" aria-hidden="true">#</a></h2><p>\u7EC4\u7EC7\u5C5E\u6027\uFF0C\u51B3\u5B9A\u4E86\u7BA1\u7406\u8005\u548C\u5458\u5DE5\uFF0C\u4E00\u5B9A\u6709\u4E0D\u53EF\u8C03\u548C\u7684\u5229\u76CA\u51B2\u7A81\u70B9\u3002</p><p>\u80FD\u5426\u8BA9\u7EC4\u7EC7\u76EE\u6807\u548C\u4E2A\u4EBA\u76EE\u6807\u6301\u7EED\u7EDF\u4E00\uFF0C\u662F\u8861\u91CF\u7BA1\u7406\u6C34\u5E73\u7684\u552F\u4E00\u6807\u51C6\u3002</p><p>\u96BE\u7684\u4E0D\u662F\u4E2A\u4EBA\u76EE\u6807\uFF0C\u800C\u662F\u8C03\u52A8\u6BCF\u4E2A\u5F7C\u6B64\u4E0D\u540C\u7684\u4EBA\uFF0C\u6700\u7EC8\u5B8C\u6210\u56E2\u961F\u76EE\u6807\u3002</p><p>\u8C01\u7AD9\u5728\u7BA1\u7406\u8005\u7684\u89D2\u5EA6\uFF0C\u5C31\u5FC5\u987B\u5BF9\u7EC4\u7EC7\u8D1F\u8D23\u3002</p><p>\u5F53\u6210\u4E3A\u7BA1\u7406\u8005\u7684\u90A3\u4E00\u523B\uFF0C\u5DE5\u4F5C\u4E2D\u7684\u4E00\u5207\u89C2\u70B9\u548C\u884C\u4E3A\uFF0C\u90FD\u5C06\u4EE3\u8868\u6574\u4E2A\u56E2\u961F\uFF0C\u800C\u4E0D\u662F\u81EA\u5DF1\u3002</p><h2 id="\u7BA1\u7406\u5C31\u662F\u628A\u4EBA\u548C\u4E8B\u505A\u5230\u5145\u5206\u7ED3\u5408" tabindex="-1">\u7BA1\u7406\u5C31\u662F\u628A\u4EBA\u548C\u4E8B\u505A\u5230\u5145\u5206\u7ED3\u5408 <a class="header-anchor" href="#\u7BA1\u7406\u5C31\u662F\u628A\u4EBA\u548C\u4E8B\u505A\u5230\u5145\u5206\u7ED3\u5408" aria-hidden="true">#</a></h2><p>\u4ECE\u4EBA\u7684\u672C\u6027\u6765\u8BB2\uFF0C\u6CA1\u6709\u4EBA\u559C\u6B22\u88AB\u7BA1\u7406\uFF0C\u66F4\u6CA1\u6709\u4EBA\u559C\u6B22\u88AB\u5B9A\u4E49\u3002</p><p>\u54EA\u6015\u662F\u5DE5\u4F5C\u6210\u7EE9\u771F\u7684\u4E0D\u597D\u7684\u5458\u5DE5\uFF0C\u4ED6\u4EEC\u5FC3\u91CC\u4E5F\u8BA4\u4E3A\uFF0C\u81EA\u5DF1\u5F88\u60F3\u628A\u4E8B\u60C5\u505A\u597D\uFF0C\u81EA\u5DF1\u5E76\u4E0D\u662F\u4E0D\u52AA\u529B\u3002</p><p>\u8FD9\u5C31\u662F\u4EBA\u6027\uFF0C\u5F88\u5C11\u6709\u4EBA\u4F1A\u627F\u8BA4\u81EA\u5DF1\u662F\u4E0D\u601D\u8FDB\u53D6\u7684\u3001\u4E0D\u6C42\u4E0A\u8FDB\u7684\u3002</p><p>\u4F18\u79C0\u7684\u7BA1\u7406\u8005\uFF0C\u90FD\u61C2\u5F97\u7814\u7A76\u4EBA\u6027\u3001\u5C0A\u91CD\u4EBA\u6027\uFF0C\u800C\u4E0D\u662F\u5BF9\u6297\u4EBA\u6027\u3002</p><p>\u4ECE\u5B9E\u9645\u5DE5\u4F5C\u51FA\u53D1\uFF0C\u8BA9\u81EA\u5DF1\u7684\u957F\u5904\u5F97\u5230\u53D1\u6325\uFF0C\u505A\u81EA\u5DF1\u64C5\u957F\u7684\u4E8B\u3002</p><p>\u7ECF\u9A8C\u8D8A\u4E30\u5BCC\u7684\u7BA1\u7406\u8005\u7EA6\u660E\u767D\uFF0C\u4EBA\u662F\u7BA1\u4E0D\u4F4F\u7684\uFF0C\u8981\u60F3\u7BA1\u4F4F\u4EBA\uFF0C\u9996\u5148\u8981\u7BA1\u4F4F\u4E8B\u3002</p><p>\u5F88\u591A\u4F01\u4E1A\u53D1\u5C55\u5230\u4E00\u5B9A\u89C4\u6A21\u540E\uFF0C\u90FD\u4F1A\u5F00\u59CB\u6CE8\u91CD\u6D41\u7A0B\u7BA1\u7406\uFF0C\u672C\u8D28\u4E0A\u662F\u4E0D\u518D\u4F9D\u8D56\u4EBA\u7684\u4E3B\u89C2\u80FD\u52A8\u6027\u3002</p><p>\u6D41\u7A0B\u7BA1\u7406\uFF0C\u5F88\u591A\u7BA1\u7406\u8005\u90FD\u4E0D\u592A\u613F\u610F\u505A\uFF0C\u56E0\u4E3A\u9700\u8981\u601D\u8003\u7684\u95EE\u9898\u6BD4\u8F83\u591A\u3002</p><p>\u4F46\u628A\u4E8B\u5B9A\u6E05\u695A\uFF0C\u662F\u6BCF\u4E00\u4E2A\u7BA1\u7406\u8005\u5E94\u6709\u7684\u804C\u8D23\u3002</p><p>\u5728\u7BA1\u4E8B\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u8981\u5C0A\u91CD\u3001\u7406\u89E3\u548C\u5173\u5FC3\u4EBA\uFF0C\u624D\u80FD\u907F\u514D\u56E2\u961F\u51DD\u805A\u529B\u7684\u4E0B\u964D\u3002</p><p>\u7BA1\u7406\u5B66\u4E0A\u8BB2\uFF0C\u4EBA\u662F\u5F88\u96BE\u6539\u53D8\u7684\uFF0C\u5BF9\u4E8E\u6001\u5EA6\u6709\u95EE\u9898\u7684\u4E0B\u5C5E\uFF0C\u8981\u53CA\u65F6\u8F9E\u9000\u3002</p><h2 id="\u964D\u4F4E\u4E0B\u5C5E\u7684\u5DE5\u4F5C\u96BE\u5EA6\u5E76\u6FC0\u53D1\u5176\u79EF\u6781\u6027" tabindex="-1">\u964D\u4F4E\u4E0B\u5C5E\u7684\u5DE5\u4F5C\u96BE\u5EA6\u5E76\u6FC0\u53D1\u5176\u79EF\u6781\u6027 <a class="header-anchor" href="#\u964D\u4F4E\u4E0B\u5C5E\u7684\u5DE5\u4F5C\u96BE\u5EA6\u5E76\u6FC0\u53D1\u5176\u79EF\u6781\u6027" aria-hidden="true">#</a></h2><p>\u5458\u5DE5\u7684\u79EF\u6781\u6027\u4E3B\u8981\u7531\u4E3B\u89C2\u610F\u8BC6\u548C\u5916\u90E8\u73AF\u5883\u8FD9\u4E24\u65B9\u9762\u51B3\u5B9A\u3002</p><p>\u5916\u90E8\u73AF\u5883\uFF0C\u4E3B\u8981\u7531\u516C\u53F8\u548C\u56E2\u961F\u8D1F\u8D23\u4EBA\u7684\u7BA1\u7406\u6C34\u5E73\u51B3\u5B9A\uFF0C\u5305\u62EC\uFF1A\u5C97\u4F4D\u804C\u8D23\u662F\u5426\u5408\u7406\u3001\u6FC0\u52B1\u673A\u5236\u662F\u5426\u6E05\u6670\u3001\u664B\u5347\u7A7A\u95F4\u662F\u5426\u660E\u786E\u7B49\u3002</p><p>\u800C\u4E3B\u89C2\u610F\u8BC6\uFF0C\u5C31\u662F\u4E00\u540D\u5458\u5DE5\u5BF9\u81EA\u6211\u6210\u957F\u7684\u671F\u5F85\u548C\u5BF9\u81EA\u8EAB\u884C\u4E3A\u7684\u8BA4\u77E5\u3002</p><p>\u4E00\u4E2A\u4EBA\u7684\u4E3B\u89C2\u610F\u8BC6\uFF0C\u957F\u65F6\u95F4\u79EF\u7D2F\u4E4B\u540E\uFF0C\u4F1A\u6C89\u6DC0\u6210\u4E00\u4E2A\u4EBA\u7684\u57FA\u672C\u7D20\u8D28\u3002</p><p>\u800C\u4E00\u4E2A\u4EBA\u7684\u57FA\u672C\u7D20\u8D28\u662F\u5F88\u96BE\u6539\u53D8\u7684\uFF0C\u4F60\u770B\u5230\u7684\u5C31\u662F\u4F60\u770B\u5230\u7684\u3002</p><p>\u6240\u4EE5\u5728\u57F9\u517B\u4EBA\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4E0D\u8981\u8BD5\u56FE\u6539\u53D8\u4E00\u4E2A\u4EBA\u7684\u57FA\u672C\u7D20\u8D28\uFF0C\u800C\u662F\u8981\u627E\u5230\u7D20\u8D28\u548C\u80FD\u529B\u7684\u7ED3\u5408\u70B9\u3002</p><p>\u8D8A\u6210\u529F\u7684\u4F01\u4E1A\uFF0C\u5728\u62DB\u8058\u65F6\uFF0C\u8D8A\u770B\u91CD\u813E\u6C14\u3001\u79C9\u6027\u7B49\u57FA\u672C\u7D20\u8D28\uFF0C\u7136\u540E\u518D\u628A\u4ED6\u4EEC\u57F9\u517B\u6210\u516C\u53F8\u9700\u8981\u7684\u4EBA\u3002</p><p>\u4F5C\u4E3A\u4E00\u540D\u7BA1\u7406\u8005\uFF0C\u8BC4\u4EF7\u5458\u5DE5\u548C\u9009\u62D4\u4EBA\u624D\u662F\u91CD\u8981\u7684\u4E00\u6B65\uFF0C\u4E3B\u8981\u5F62\u5F0F\u662F\u4E00\u5BF9\u4E00\u6C9F\u901A\u3002</p><p>\u901A\u8FC7\u6C9F\u901A\uFF0C\u4E86\u89E3\u4E00\u4E2A\u5458\u5DE5\u5BF9\u81EA\u6211\u6210\u957F\u7684\u671F\u5F85\u548C\u5BF9\u81EA\u6211\u884C\u4E3A\u7684\u8BA4\u77E5\uFF0C\u4ECE\u800C\u627E\u51C6\u5458\u5DE5\u7684\u4E3B\u89C2\u610F\u8BC6\u548C\u57FA\u672C\u7D20\u8D28\u3002</p><p>\u4ED6\u4EEC\u7684\u4E3B\u89C2\u610F\u8BC6\u548C\u57FA\u672C\u7D20\u8D28\u662F\u5F88\u96BE\u88AB\u6539\u53D8\u7684\uFF0C\u6240\u4EE5\u6CA1\u5FC5\u8981\u6253\u592A\u591A\u7684\u9E21\u8840\u3002</p><h2 id="\u4E3A\u5927\u5BB6\u6811\u7ACB\u4E00\u4E2A\u597D\u7684\u699C\u6837\u6216\u5408\u7406\u7684\u76EE\u6807" tabindex="-1">\u4E3A\u5927\u5BB6\u6811\u7ACB\u4E00\u4E2A\u597D\u7684\u699C\u6837\u6216\u5408\u7406\u7684\u76EE\u6807 <a class="header-anchor" href="#\u4E3A\u5927\u5BB6\u6811\u7ACB\u4E00\u4E2A\u597D\u7684\u699C\u6837\u6216\u5408\u7406\u7684\u76EE\u6807" aria-hidden="true">#</a></h2><p>\u4E00\u540D\u5408\u683C\u7684\u7BA1\u7406\u8005\uFF0C\u5E94\u8BE5\u80FD\u591F\u5E2E\u52A9\u5458\u5DE5\uFF0C\u66F4\u597D\u5730\u4E86\u89E3\u81EA\u5DF1\u3002</p><p>\u901A\u8FC7\u5B9A\u671F\u7684\u4E00\u5BF9\u4E00\u6C9F\u901A\uFF0C\u9010\u6B65\u5E2E\u52A9\u5458\u5DE5\u6811\u7ACB\u4E00\u4E2A\u5408\u7406\u7684\u76EE\u6807\uFF0C\u6216\u8005\u627E\u5230\u4E00\u4E2A\u5408\u9002\u7684\u699C\u6837\u3002</p><p>\u8D8A\u6210\u529F\u7684\u4EBA\uFF0C\u4F1A\u8D8A\u65E9\u6811\u7ACB\u81EA\u5DF1\u6E05\u6670\u7684\u699C\u6837\u3002</p><p>\u5F53\u5927\u5BB6\u975E\u5E38\u6E05\u695A\u81EA\u5DF1\u7684\u76EE\u6807\uFF0C\u4E5F\u80FD\u627E\u5230\u81EA\u5DF1\u5B66\u4E60\u7684\u699C\u6837\u65F6\uFF0C\u5C31\u4F1A\u81EA\u89C9\u5730\u671D\u90A3\u4E2A\u76EE\u6807\u53BB\u52AA\u529B\uFF0C\u8FD8\u4F1A\u5728\u65E5\u5E38\u5DE5\u4F5C\u4E2D\uFF0C\u4E0D\u65AD\u5730\u6A21\u4EFF\u699C\u6837\u7684\u884C\u4E3A\u3002</p><p>\u5F53\u8FD9\u4E9B\u4E8B\u60C5\u8D8A\u6765\u8D8A\u591A\u53D1\u751F\u65F6\uFF0C\u4ED6\u4EEC\u4E5F\u4F1A\u53D7\u5230\u6765\u81EA\u8EAB\u8FB9\u4EBA\u7684\u8BA4\u53EF\u548C\u9F13\u52B1\u3002</p><p>\u4E00\u6D41\u7684\u7BA1\u7406\u8005\u90FD\u77E5\u9053\uFF0C\u5931\u8D25\u662F\u4E0D\u53EF\u907F\u514D\u7684\uFF0C\u751A\u81F3\u4ED6\u4EEC\u7684\u76EE\u6807\u4E5F\u4E0D\u662F\u4E00\u5B9A\u6210\u529F\u7684\uFF0C\u6240\u4EE5\u8981\u53CA\u65F6\u6D88\u9664\u5927\u5BB6\u5BB3\u6015\u5931\u8D25\u3001\u72AF\u9519\u3001\u88AB\u6279\u8BC4\u548C\u88AB\u62D2\u7EDD\u7684\u6050\u60E7\u611F\u3002</p><p>\u4F5C\u4E3A\u56E2\u961F\u8D1F\u8D23\u4EBA\uFF0C\u8981\u52AA\u529B\u8425\u9020\u4E00\u4E2A\u79EF\u6781\u3001\u5F00\u653E\u7684\u56E2\u961F\u6C1B\u56F4\uFF0C\u5927\u5BB6\u53EF\u4EE5\u516C\u5F00\u3001\u6E05\u6670\u5730\u8868\u8FBE\u81EA\u5DF1\u7684\u89C2\u70B9\uFF0C\u4E0D\u7528\u62C5\u5FC3\u7BA1\u7406\u8005\u7684\u559C\u6012\u3002</p><p>\u51FA\u73B0\u95EE\u9898\u65F6\uFF0C\u8981\u805A\u7126\u89E3\u51B3\u95EE\u9898\uFF0C\u800C\u4E0D\u662F\u6279\u8BC4\u5F53\u4E8B\u4EBA\uFF0C\u800C\u4E14\u8981\u52C7\u4E8E\u548C\u5F53\u4E8B\u4EBA\u4E00\u8D77\u627F\u62C5\u8D23\u4EFB\u3002</p>',44),h=[d];function i(s,l,n,c,o,_){return a(),e("div",null,h)}var m=p(r,[["render",i]]);export{f as __pageData,m as default};
