import{_ as e,c as a,o as p,a as i}from"./app.9aa1eb8d.js";const f='{"title":"\u5982\u4F55\u641E\u56E2\u5EFA","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E3A\u4EC0\u4E48\u56E2\u5EFA","slug":"\u4E3A\u4EC0\u4E48\u56E2\u5EFA"},{"level":2,"title":"\u76F8\u8BC6","slug":"\u76F8\u8BC6"},{"level":2,"title":"\u76F8\u77E5","slug":"\u76F8\u77E5"},{"level":2,"title":"\u76F8\u4F9D","slug":"\u76F8\u4F9D"},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3"}],"relativePath":"manage/tb.md","lastUpdated":1655551077710}',l={},t=i('<h1 id="\u5982\u4F55\u641E\u56E2\u5EFA" tabindex="-1">\u5982\u4F55\u641E\u56E2\u5EFA <a class="header-anchor" href="#\u5982\u4F55\u641E\u56E2\u5EFA" aria-hidden="true">#</a></h1><p>\u6211\u4EEC\u5728\u8BF4\u56E2\u5EFA\u4E4B\u524D\uFF0C\u5148\u660E\u786E\u4E24\u4E2A\u6982\u5FF5\uFF1A\u7FA4\u4F53\u4E0E\u56E2\u961F\u3002\u5728\u7BA1\u7406\u5B66\u4E0A\uFF0C\u7FA4\u4F53\uFF08Group\uFF09\u548C\u56E2\u961F\uFF08Team\uFF09\u662F\u4E24\u4E2A\u4E0D\u540C\u7684\u6982\u5FF5\u3002</p><p>\u7FA4\u4F53\uFF0C\u4E5F\u53EF\u4EE5\u88AB\u79F0\u4E3A\u201C\u56E2\u4F19\u201D\uFF0C\u5C31\u662F\u4E00\u7FA4\u4EBA\uFF0C\u6210\u5458\u4E4B\u95F4\u53EF\u4EE5\u6709\u5171\u540C\u7684\u76EE\u6807\uFF0C\u4F46\u5F7C\u6B64\u4E4B\u95F4\u6CA1\u6709\u4EC0\u4E48\u5173\u8054\u3002</p><p>\u56E2\u961F\uFF0C\u662F\u4E00\u7FA4\u6709\u5171\u540C\u76EE\u6807\u3001\u6210\u5458\u4E4B\u95F4\u6709\u534F\u540C\u914D\u5408\u5173\u7CFB\u7684\u7EC4\u7EC7\u3002\u6210\u5458\u4E4B\u95F4\u6709\u914D\u5408\u3001\u6709\u9ED8\u5951\uFF0C\u662F\u56E2\u961F\u548C\u7FA4\u4F53\u6700\u5927\u7684\u5206\u522B\u3002</p><h2 id="\u4E3A\u4EC0\u4E48\u56E2\u5EFA" tabindex="-1">\u4E3A\u4EC0\u4E48\u56E2\u5EFA <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u56E2\u5EFA" aria-hidden="true">#</a></h2><p>\u56E2\u961F\u6700\u91CD\u8981\u7684\u7279\u5F81\uFF0C\u5C31\u662F\u6210\u5458\u4E4B\u95F4\u5B58\u5728\u4E92\u52A8\uFF0C\u826F\u6027\u4E92\u52A8\u521B\u9020\u534F\u540C\u6548\u5E94\u3002\u5982\u679C\u4E24\u4E2A\u72EC\u7ACB\u7684\u4E2A\u4F53\uFF0C\u4E00\u52A0\u4E00\u7B49\u4E8E\u4E8C\uFF0C\u5C31\u662F\u6CA1\u6709\u534F\u540C\u6548\u5E94\u3002\u800C\u4E00\u52A0\u4E00\u5927\u4E8E\u4E8C\uFF0C\u5C31\u6709\u534F\u540C\u6548\u5E94\uFF0C\u591A\u51FA\u6765\u7684\u90A3\u4E2A\u90E8\u5206\uFF0C\u5C31\u662F\u534F\u540C\u6548\u5E94\u7684\u7ED3\u679C\u3002</p><p>\u5F88\u591A\u516C\u53F8\uFF0C\u4E3A\u4E86\u5927\u5BB6\u548C\u7766\u76F8\u5904\u800C\u641E\u56E2\u961F\u5EFA\u8BBE\uFF0C\u516C\u8D39\u5403\u5403\u559D\u559D\uFF0C\u751A\u81F3\u518D\u6765\u4E00\u4E9B\u6237\u5916\u6D3B\u52A8\uFF0C\u51FA\u53BB\u4E00\u8D77\u73A9\u4E00\u73A9\uFF0C\u7ED3\u679C\u82B1\u4E86\u5F88\u591A\u94B1\uFF0C\u6D6A\u8D39\u4E86\u5F88\u591A\u65F6\u95F4\uFF0C\u4F46\u4E00\u56E2\u548C\u6C14\u7684\u80CC\u540E\uFF0C\u4E1A\u7EE9\u6F5C\u529B\u5E76\u6CA1\u6709\u6316\u6398\u51FA\u6765\uFF0C\u72AF\u4E86\u65B9\u5411\u6027\u7684\u9519\u8BEF\u3002</p><p>\u6211\u4EEC\u8FDB\u884C\u56E2\u961F\u5EFA\u8BBE\u7684\u76EE\u7684\u662F\u4EC0\u4E48\uFF1F\u6709\u4EBA\u89C9\u5F97\u662F\u8981\u5EFA\u7ACB\u4E00\u4E2A\u5BB6\u5EAD\u822C\u7684\u3001\u4EB2\u5982\u5144\u5F1F\u7684\u7EC4\u7EC7\u3002\u7F51\u98DE\u5C31\u7279\u522B\u660E\u786E\u5730\u8BF4\u201Cwe are not family\u201D\uFF0C\u6211\u4EEC\u4E0D\u662F\u4E00\u4E2A\u5BB6\u5EAD\uFF0C\u6211\u4EEC\u4E0D\u662F\u5BB6\u4EBA\u3002\u6211\u4EEC\u7684\u76EE\u7684\u662F\u5408\u8D77\u4F19\u6765\u8D5A\u94B1\u3002\u6240\u4EE5\uFF0C\u7F51\u98DE\u7684\u56E2\u961F\u5EFA\u8BBE\u662F\u7ADE\u4E89\u81F3\u4E0A\u7684\u3002</p><p>\u4E00\u5BB6\u516C\u53F8\uFF0C\u5982\u679C\u80FD\u591F\u4E00\u65B9\u9762\u8FBE\u6210\u76EE\u6807\uFF0C\u540C\u65F6\u4E5F\u80FD\u5EFA\u7ACB\u6210\u4E00\u4E2A\u5927\u5BB6\u5EAD\u6700\u597D\u3002\u5982\u679C\u505A\u4E0D\u5230\uFF0C\u4F60\u4E00\u5B9A\u8981\u77E5\u9053\u81EA\u5DF1\u60F3\u8981\u4EC0\u4E48\uFF0C\u77E5\u9053\u4E3A\u7684\u662F\u4EC0\u4E48\u3002</p><h2 id="\u76F8\u8BC6" tabindex="-1">\u76F8\u8BC6 <a class="header-anchor" href="#\u76F8\u8BC6" aria-hidden="true">#</a></h2><p>\u770B\u4E00\u4E0B\u56E2\u961F\u662F\u5426\u5B58\u5728\u4E0B\u9762\u7684\u96C6\u4E2D\u60C5\u51B5\uFF1A</p><ul><li>\u56E2\u961F\u6210\u5458\u5F7C\u6B64\u4E0D\u719F\u6089\uFF0C\u5E73\u65F6\u6C9F\u901A\u8FC7\u4E8E\u5BA2\u6C14\uFF0C\u4E00\u65E6\u9047\u5230\u5206\u6B67\uFF0C\u5F88\u5BB9\u6613\u53D1\u751F\u4E89\u5435</li><li>\u5403\u996D\u4F11\u606F\u7684\u65F6\u5019\uFF0C\u56E2\u961F\u6210\u5458\u5206\u6210\u56FA\u5B9A\u7684\u51E0\u6CE2\u4E0D\u540C\u5C0F\u56E2\u961F\uFF0C\u73A9\u4E0D\u5230\u4E00\u5757\uFF0C\u8FD8\u6709\u4E2A\u522B\u7684\u540C\u4E8B\u88AB\u5B64\u7ACB</li><li>\u4F60\u4E0D\u4E86\u56E2\u961F\u6210\u5458\u7684\u5174\u8DA3\u7231\u597D\uFF0C\u5927\u5BB6\u4E5F\u4E0D\u4E86\u89E3\u4F60\u7684\u3002\u5927\u5BB6\u5728\u4E00\u8D77\u804A\u5929\u7684\u8BDD\u9898\u90FD\u548C\u5DE5\u4F5C\u76F8\u5173</li></ul><p>\u5982\u679C\u6709\u4E0A\u9762\u7684\u51E0\u79CD\u60C5\u51B5\u6216\u8005\u7C7B\u4F3C\u7684\u60C5\u51B5\uFF0C\u8BF4\u660E\u56E2\u961F\u8FD8\u5904\u4E8E\u516C\u4E8B\u516C\u529E\u7684\u72B6\u6001\uFF0C\u56E2\u961F\u6210\u5458\u95F4\u6CA1\u6709\u4EFB\u4F55\u7684\u611F\u60C5\u8FDE\u63A5\u3002</p><p>\u5927\u5BB6\u5F7C\u6B64\u4E0D\u719F\u6089\uFF0C\u50CF\u9F7F\u8F6E\u95F4\u7F3A\u5C11\u4E86\u6DA6\u6ED1\u5242\uFF0C\u7ECF\u5E38\u4F1A\u4E3A\u4E00\u4E9B\u5C0F\u4E8B\u4E89\u5435\uFF0C\u56E2\u961F\u6210\u5458\u7684\u72B6\u51B5\u9886\u5BFC\u4E0D\u6E05\u695A\uFF0C\u6210\u5458\u8981\u79BB\u804C\u4E86\uFF0C\u9886\u5BFC\u6700\u540E\u624D\u77E5\u9053\u3002</p><p>\u5728\u8FD9\u79CD\u60C5\u51B5\uFF0C\u6211\u4EEC\u9700\u8981\u7B2C\u4E00\u79CD\u7C7B\u578B\u7684\u56E2\u5EFA\u2014\u2014\u76F8\u8BC6\u3002\u8FD9\u79CD\u56E2\u5EFA\u9700\u8981\u5927\u5BB6\u5145\u5206\u4E86\u89E3\uFF0C\u6210\u4E3A\u670B\u53CB\u3002\u81F3\u4E8E\u5F62\u5F0F\uFF0C\u4E0D\u91CD\u8981\uFF0C\u91CD\u8981\u7684\u662F\u901A\u8FC7\u6D3B\u52A8\u80FD\u4E0D\u80FD\u589E\u8FDB\u5F7C\u6B64\u7684\u8BA4\u8BC6\uFF0C\u628A\u6563\u843D\u7684\u56E2\u961F\u6210\u5458\u9ECF\u5728\u4E00\u8D77\u3002\u6211\u4E4B\u524D\u5E38\u7528\u7684\u65B9\u5F0F\u6BD4\u5982\u641E\u4E00\u4E9B\u6709\u5171\u540C\u5174\u8DA3\u7684\u4F53\u80B2\u6D3B\u52A8\uFF0C\u5C24\u5176\u662F\u56E2\u4F53\u7C7B\u7684\u4F53\u80B2\u6D3B\u52A8\u3002</p><p>\u805A\u9910\uFF0C\u8BB2\u4E2A\u4EBA\u771F\u5B9E\u7684\u6545\u4E8B\uFF0C\u627E\u5927\u5BB6\u5171\u540C\u7684\u8BDD\u9898\u3002\u8BB2\u6545\u4E8B\u8FD9\u5757\uFF0C\u6211\u4EEC\u53EF\u4EE5\u6574\u7406\u51E0\u4E2A\u4E3B\u9898\uFF0C\u6BD4\u5982\u4EBA\u751F\u4E2D\u6700\u91CD\u8981\u7684\u4E00\u4EF6\u4E8B\uFF0C\u6700\u5F00\u5FC3\u7684\uFF0C\u6700\u96BE\u8FC7\u7684\uFF0C\u6700\u9057\u61BE\u7684\u7B49\u7B49\uFF0C\u4E00\u5B9A\u8981\u662F\u53D1\u81EA\u5185\u5FC3\u7684\u4E8B\u60C5\uFF0C\u8FD9\u65F6\u5019\u5927\u5BB6\u80FD\u591F\u6C89\u6D78\u5176\u4E2D\uFF0C\u589E\u5F3A\u5176\u4ED6\u4EBA\u4E0E\u8BB2\u6545\u4E8B\u4EBA\u7684\u4E86\u89E3\u3002</p><p>\u8FD9\u6837\u6BCF\u4E2A\u4EBA\u90FD\u8BB2\uFF0C\u6700\u7EC8\u5927\u5BB6\u4F1A\u589E\u8FDB\u76F8\u8BC6\uFF0C\u5316\u89E3\u6210\u5458\u4E4B\u95F4\u7684\u8BEF\u89E3\u548C\u654C\u610F\u3002\u641E\u8FC7\u51E0\u6B21\u4E4B\u540E\uFF0C\u540E\u9762\u5927\u5BB6\u5DE5\u4F5C\u8D77\u6765\uFF0C\u4F1A\u9010\u6E10\u653E\u4E0B\u516C\u4E8B\u516C\u529E\u7684\u72B6\u6001\u3002</p><h2 id="\u76F8\u77E5" tabindex="-1">\u76F8\u77E5 <a class="header-anchor" href="#\u76F8\u77E5" aria-hidden="true">#</a></h2><p>\u540C\u6837\uFF0C\u6211\u4EEC\u518D\u770B\u4E00\u4E0B\u56E2\u961F\u662F\u5426\u5B58\u5728\u4E0B\u9762\u7684\u60C5\u51B5\uFF1A</p><ul><li>\u5206\u914D\u4EFB\u52A1\u7684\u65F6\u5019\uFF0C\u5927\u5BB6\u90FD\u4E0D\u79EF\u6781\uFF0C\u63A8\u4E09\u963B\u56DB\u3002\u5B8C\u4E0D\u6210\u4EFB\u52A1\u7684\u65F6\u5019\uFF0C\u603B\u7231\u627E\u501F\u53E3\u3002</li><li>\u4E0B\u73ED\u7684\u65F6\u5019\u7ECF\u5E38\u51C6\u65F6\u8D70\uFF0C\u4E0D\u613F\u610F\u4E3A\u5DE5\u4F5C\u4ED8\u51FA\u989D\u5916\u6295\u5165</li><li>\u56E2\u961F\u6210\u5458\u4E3A\u5404\u81EA\u7684KPI\u4E89\u5435\u4E0D\u4F11</li></ul><p>\u5982\u679C\u6709\u4E0A\u9762\u7684\u51E0\u79CD\u60C5\u51B5\u6216\u8005\u7C7B\u4F3C\u7684\u60C5\u51B5\uFF0C\u8BF4\u660E\u56E2\u961F\u6216\u6210\u5458\u5BF9\u4E8E\u81EA\u5DF1\u5DE5\u4F5C\u7684\u610F\u4E49\u6CA1\u6709\u611F\u77E5\u3002\u5927\u5BB6\u53EA\u662F\u91CD\u590D\u624B\u5934\u4E0A\u7684\u5DE5\u4F5C\uFF0C\u76EE\u524D\u7684\u5DE5\u4F5C\u4E5F\u53EA\u662F\u4E00\u4EFD\u5DE5\u4F5C\u800C\u5DF2\uFF0C\u6CA1\u6709\u628A\u804C\u4E1A\u5F53\u4F5C\u4E8B\u4E1A\u3002</p><p>\u8FD9\u65F6\u5019\u5C31\u9700\u8981\u628A\u5DE5\u4F5C\u7684\u610F\u4E49\u63D0\u70BC\u51FA\u6765\uFF0C\u5E76\u4E0D\u65AD\u5BA3\u8BB2\u89E6\u8FBE\u3002\u8981\u8BA4\u6E05\u695A\u6211\u4EEC\u7684\u4F7F\u547D\u548C\u613F\u666F\uFF0C\u8BA9\u5927\u5BB6\u5728\u8FD9\u4EF6\u4E8B\u60C5\u4E0A\u8FBE\u6210\u5171\u8BC6\u3002</p><p>\u4F7F\u547D\u662F\u5229\u4ED6\u7684\uFF0C\u627E\u5230\u56E2\u961F\u7684\u670D\u52A1\u5BF9\u8C61\uFF0C\u63D0\u70BC\u51FA\u4E3A\u670D\u52A1\u5BF9\u8C61\u521B\u9020\u4E86\u4EC0\u4E48\u6837\u7684\u4EF7\u503C\u3002\u613F\u666F\u662F\u5229\u5DF1\u7684\uFF0C\u6211\u4EEC\u7684\u56E2\u961F\u4E0D\u65AD\u89E3\u51B3\u8FD9\u7C7B\u95EE\u9898\u540E\u4F1A\u53D8\u6210\u600E\u6837\u7684\u56E2\u961F\u3002</p><p>\u76F8\u77E5\uFF0C\u5C31\u662F\u56E2\u961F\u6709\u660E\u786E\u7684\u76EE\u6807\u548C\u613F\u666F\uFF0C\u6210\u4E3A\u961F\u53CB\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u9700\u8981\u7EC4\u7EC7\u76F8\u77E5\u7684\u56E2\u5EFA\uFF0C\u8BA9\u5927\u5BB6\u77E5\u9053\u5DE5\u4F5C\u7684\u610F\u4E49\u548C\u56E2\u961F\u7684\u613F\u666F\u3002\u6BD4\u5982\u4E00\u8D77\u53BB\u62DC\u8BBF\u5BA2\u6237\uFF0C\u4ECE\u5BA2\u6237\u90A3\u91CC\u5F97\u5230\u6211\u4EEC\u4EA7\u54C1\u7ED9\u4ED6\u4EEC\u5E26\u6765\u7684\u4EF7\u503C\uFF0C\u89E3\u51B3\u7684\u95EE\u9898\u3002</p><p>\u8BA9\u56E2\u961F\u6210\u5458\u8BA4\u8BC6\u5230\u81EA\u5DF1\u5DE5\u4F5C\u7684\u4EF7\u503C\uFF0C\u53E6\u5916\u6211\u4EEC\u8BA2\u7ACB\u66F4\u9AD8\u7684\u76EE\u6807\uFF0C\u628A\u56E2\u961F\u51DD\u7ED3\u5230\u4E00\u8D77\u3002</p><h2 id="\u76F8\u4F9D" tabindex="-1">\u76F8\u4F9D <a class="header-anchor" href="#\u76F8\u4F9D" aria-hidden="true">#</a></h2><p>\u56E2\u961F\u4E2D\u662F\u5426\u6709\u7C7B\u4F3C\u7684\u95EE\u9898\uFF1A</p><ul><li>\u56E2\u961F\u6210\u5458\u4E3B\u52A8\u79BB\u804C\u7387\u7A81\u7136\u5F00\u59CB\u6500\u5347</li><li>\u56E2\u961F\u6210\u5458\u5BF9\u5DE5\u4F5C\u51FA\u73B0\u5BA1\u7F8E\u75B2\u52B3\uFF0C\u63D0\u4E0D\u8D77\u5174\u8DA3</li><li>\u5F88\u591A\u6210\u5458\u63D0\u51FA\u8F6C\u5C97</li></ul><p>\u56E2\u961F\u6210\u5458\u5DF2\u7ECF\u662F\u597D\u961F\u53CB\u4E86\uFF0C\u4F46\u8FD8\u662F\u4F1A\u51FA\u73B0\u4E0A\u8FF0\u7C7B\u4F3C\u7684\u95EE\u9898\u3002\u8BF4\u660E\u56E2\u961F\u6210\u5458\u4E4B\u95F4\u7684\u5173\u7CFB\u9700\u8981\u66F4\u8FDB\u4E00\u6B65\uFF0C\u5982\u4F55\u4ECE\u961F\u53CB\u53D8\u6210\u60A3\u96BE\u4E0E\u5171\u7684\u6218\u53CB\uFF0C\u76F8\u4E92\u6276\u6301\uFF0C\u76F8\u4E92\u4F9D\u9760\u3002\u8FD9\u65F6\u5019\uFF0C\u56E2\u961F\u9700\u8981\u6253\u5927\u4ED7\uFF0C\u9700\u8981\u4E00\u573A\u573A\u7684\u80DC\u5229\u7684\u78E8\u7EC3\uFF0C\u8BA9\u56E2\u961F\u6709\u65B0\u9C9C\u611F\u548C\u6210\u5C31\u611F\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u6211\u4EEC\u7684\u56E2\u5EFA\u9700\u8981\u5927\u5BB6\u4E00\u8D77\u5B9A\u4E49\u80DC\u5229\uFF0C\u8425\u9020\u56E2\u961F\u6253\u80DC\u4ED7\u7684\u72B6\u6001\u3002\u5F53\u7136\uFF0C\u5982\u679C\u80DC\u5229\u5B9A\u4E49\u7684\u4E0D\u6070\u5F53\uFF0C\u56E2\u961F\u58EB\u6C14\u4E0D\u597D\uFF0C\u5F88\u96BE\u63D0\u5347\u6574\u4F53\u7684\u4E1A\u7EE9\u3002\u6240\u4EE5\uFF0C\u8FD9\u65F6\u7684\u56E2\u5EFA\uFF0C\u5C31\u8981\u5EFA\u7ACB\u5728\u5B9A\u4E49\u80DC\u5229\uFF0C\u5B9A\u76EE\u6807\u4E0A\u9762\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u56E2\u961F\u5EFA\u8BBE\u8981\u7ED3\u5408\u56E2\u961F\u81EA\u5DF1\u7684\u60C5\u51B5\uFF0C\u672C\u8EAB\u5904\u4E8E\u54EA\u4E2A\u9636\u6BB5\uFF0C\u8FDB\u884C\u54EA\u4E2A\u9636\u6BB5\u7684\u56E2\u5EFA\u3002\u53E6\u5916\u5904\u4E8E\u9AD8\u7EA7\u522B\u9636\u6BB5\u7684\u56E2\u961F\u4E5F\u4E0D\u662F\u8BF4\u5C31\u4E0D\u9700\u8981\u4F4E\u7EA7\u522B\u7684\u56E2\u5EFA\u7C7B\u578B\uFF0C\u53EF\u4EE5\u6839\u636E\u6839\u636E\u5177\u4F53\u60C5\u51B5\u548C\u5177\u4F53\u7684\u65F6\u95F4\u7075\u6D3B\u9009\u62E9\u3002</p><p>\u4E0D\u8FC7\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5904\u4E8E\u4F4E\u7EA7\u522B\u9636\u6BB5\u7684\u56E2\u961F\uFF0C\u5C3D\u91CF\u4E0D\u8981\u7EC4\u7EC7\u9AD8\u7EA7\u522B\u7684\u56E2\u5EFA\uFF0C\u5F88\u96BE\u7EC4\u7EC7\u597D\u3002</p>',34),r=[t];function d(h,s,n,c,o,_){return p(),a("div",null,r)}var b=e(l,[["render",d]]);export{f as __pageData,b as default};
