import{_ as e,c as l,o as i,a}from"./app.256e99ba.js";const f='{"title":"\u8FB9\u7F18\u8BA1\u7B97\u4E0E\u7AEF\u667A\u80FD","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8FB9\u7F18\u8BA1\u7B97","slug":"\u8FB9\u7F18\u8BA1\u7B97"},{"level":2,"title":"\u7AEF\u667A\u80FD","slug":"\u7AEF\u667A\u80FD"},{"level":3,"title":"\u7AEF\u667A\u80FD\u5E94\u7528","slug":"\u7AEF\u667A\u80FD\u5E94\u7528"},{"level":3,"title":"\u7AEF\u667A\u80FD\u6311\u6218","slug":"\u7AEF\u667A\u80FD\u6311\u6218"},{"level":3,"title":"\u7AEF\u667A\u80FD\u8D8B\u52BF","slug":"\u7AEF\u667A\u80FD\u8D8B\u52BF"},{"level":3,"title":"\u540E\u6469\u5C14\u65F6\u4EE3\u4E0EXPU","slug":"\u540E\u6469\u5C14\u65F6\u4EE3\u4E0Expu"},{"level":3,"title":"\u7531\u624B\u673A\u7AEF\u5230AIOT\u7AEF","slug":"\u7531\u624B\u673A\u7AEF\u5230aiot\u7AEF"},{"level":3,"title":"\u7AEF\u667A\u80FD\u6846\u67B6\u53D1\u5C55","slug":"\u7AEF\u667A\u80FD\u6846\u67B6\u53D1\u5C55"},{"level":2,"title":"\u53C2\u8003","slug":"\u53C2\u8003"}],"relativePath":"tech/edge.md","lastUpdated":1681349490013}',t={},r=a('<h1 id="\u8FB9\u7F18\u8BA1\u7B97\u4E0E\u7AEF\u667A\u80FD" tabindex="-1">\u8FB9\u7F18\u8BA1\u7B97\u4E0E\u7AEF\u667A\u80FD <a class="header-anchor" href="#\u8FB9\u7F18\u8BA1\u7B97\u4E0E\u7AEF\u667A\u80FD" aria-hidden="true">#</a></h1><p>\u65E5\u76CA\u589E\u957F\u7684\u4E1A\u52A1\u5B9E\u65F6\u6027\u9700\u6C42\u4F7F\u8FB9\u7F18\u548C\u7AEF\u4FA7\u8BA1\u7B97\u80FD\u529B\u53D8\u5F97\u8D8A\u6765\u8D8A\u91CD\u8981\uFF0CIDC \u9884\u6D4B\uFF0C\u5230 2023 \u5E74\uFF0C\u63A5\u8FD1 20% \u7684\u7528\u4E8E\u4EBA\u5DE5\u667A\u80FD\u5DE5\u4F5C\u8D1F\u8F7D\u7684\u670D\u52A1\u5668\u5C06\u90E8\u7F72\u5728\u8FB9\u7F18\u3002</p><p>\u4E91\u8BA1\u7B97\u4E3A\u4EBA\u5DE5\u667A\u80FD\u63D0\u4F9B\u4E86\u57FA\u7840\u67B6\u6784\uFF0C\u76EE\u524D\u4E91\u7AEF\u662F\u627F\u8F7D\u6D77\u91CF\u6570\u636E\u5904\u7406\u548C\u5927\u89C4\u6A21\u8BA1\u7B97\u7684\u91CD\u8981\u8F7D\u4F53\u3002\u5728\u65B0\u6280\u672F\u7684\u9A71\u52A8\u4E0B\uFF0C5G \u7B49\u8FDE\u63A5\u6280\u672F\u964D\u4F4E\u4E86\u6570\u636E\u7684\u4F20\u8F93\u548C\u5904\u7406\u901F\u5EA6\uFF0C\u4E3A\u4E86\u5206\u62C5\u6570\u636E\u4E2D\u5FC3\u7684\u8BA1\u7B97\u538B\u529B\u5E76\u4E14\u4E5F\u80FD\u63D0\u9AD8\u5B9E\u65F6\u54CD\u5E94\u901F\u5EA6\uFF0C\u4EBA\u5DE5\u667A\u80FD\u5728\u8FB9\u7F18\u4FA7\u7684\u5904\u7406\u5C06\u6210\u4E3A\u4F01\u4E1A\u7684\u4E00\u4E2A\u5173\u952E\u589E\u957F\u9886\u57DF\u3002</p><p>\u4EBA\u5DE5\u667A\u80FD\u5728\u8FB9\u7F18\u4EE5\u53CA\u7AEF\u4FA7\u5C06\u6709\u5E7F\u6CDB\u7684\u5E94\u7528\u573A\u666F\uFF0C\u4ECE\u81EA\u52A8\u9A7E\u9A76\u5230\u5DE5\u4E1A\u5236\u9020\uFF0C\u518D\u5230\u6D88\u8D39\u8005\u667A\u80FD\u5BB6\u5C45\u548C\u53EF\u7A7F\u6234\u8BBE\u5907\uFF0C\u5904\u4E8E\u7AEF\u4FA7\u3001\u8FB9\u7F18\u4FA7\u7684\u4EBA\u5DE5\u667A\u80FD\u82AF\u7247\u9700\u8981\u5728\u529F\u8017\u3001\u8BA1\u7B97\u6027\u80FD\u3001\u5F62\u72B6\u548C\u7269\u6599\u6210\u672C\u4E4B\u95F4\u53D6\u5F97\u5E73\u8861\u3002</p><h2 id="\u8FB9\u7F18\u8BA1\u7B97" tabindex="-1">\u8FB9\u7F18\u8BA1\u7B97 <a class="header-anchor" href="#\u8FB9\u7F18\u8BA1\u7B97" aria-hidden="true">#</a></h2><p>\u4E91\u8BA1\u7B97\u6307\u7684\u662F\u901A\u8FC7\u7F51\u7EDC&quot;\u4E91&quot;\u5C06\u5DE8\u5927\u7684\u6570\u636E\u8BA1\u7B97\u5904\u7406\u7A0B\u5E8F\u5206\u89E3\u6210\u65E0\u6570\u4E2A\u5C0F\u7A0B\u5E8F\uFF0C\u7136\u540E\u901A\u8FC7\u591A\u90E8\u670D\u52A1\u5668\u7EC4\u6210\u7684\u7CFB\u7EDF\u8FDB\u884C\u5904\u7406\u548C\u5206\u6790\u8FD9\u4E9B\u5C0F\u7A0B\u5E8F\u5F97\u5230\u7ED3\u679C\u5E76\u8FD4\u56DE\u7ED9\u7528\u6237\u3002</p><p>\u4E91\u8BA1\u7B97\u53EF\u4EE5\u63D0\u4F9B\u4E86\u5E9E\u5927\u7684\u8BA1\u7B97\u80FD\u529B\u3001\u6D77\u91CF\u7684\u6570\u636E\u5B58\u50A8\u80FD\u529B\uFF0C\u6781\u5927\u7684\u4FC3\u8FDB\u4E92\u8054\u7F51\u7684\u53D1\u5C55\u3002</p><p>\u501F\u52A9\u4E91\u8BA1\u7B97\u7684\u5F3A\u5927\u8BA1\u7B97\u80FD\u529B\uFF0C\u5F88\u591A\u884C\u4E1A\u5B9E\u73B0\u4E86\u8DE8\u8D8A\u5F0F\u53D1\u5C55\uFF0C\u4F8B\u5982\u7269\u8054\u7F51\u884C\u4E1A\u7269\u8054\u7F51\u8BBE\u5907\u8FDE\u63A5\u6570\u91CF\u548C\u4EA7\u751F\u7684\u6570\u636E\u5448\u6D77\u91CF\u589E\u957F\u8D8B\u52BF\u3002</p><p>\u4E00\u65B9\u9762\u4F20\u7EDF\u7684\u91C7\u7528\u96C6\u4E2D\u5904\u7406\u65B9\u5F0F\u7684\u4E91\u8BA1\u7B97\u6A21\u578B\u5DF2\u65E0\u6CD5\u5904\u7406\u56E0\u5404\u7C7B\u63A5\u5165\u8BBE\u5907\u4EA7\u751F\u7684\u6D77\u91CF\u6570\u636E\uFF0C\u8BA9\u4E91\u7AEF\u4E0D\u582A\u91CD\u8D1F\uFF0C\u9020\u6210\u66F4\u5927\u7684\u6570\u636E\u74F6\u9888\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u4E07\u7269\u4E92\u8054\u80CC\u666F\u4E0B\uFF0C\u5E94\u7528\u670D\u52A1\u9700\u8981\u4F4E\u5EF6\u65F6\u3001\u9AD8\u53EF\u9760\u6027\u4EE5\u53CA\u6570\u636E\u5B89\u5168\uFF0C\u800C\u4F20\u7EDF\u4E91\u8BA1\u7B97\u6A21\u5F0F\u5728\u5B9E\u65F6\u6027\u3001\u9690\u79C1\u4FDD\u62A4\u548C\u80FD\u8017\u7B49\u95EE\u9898\u4E0A\u65E0\u6CD5\u6EE1\u8DB3\u9700\u6C42\u3002</p><p>\u4E3A\u4E86\u66F4\u52A0\u5B9E\u65F6\u548C\u5206\u6790\u6765\u81EA\u7EC8\u7AEF\u8BBE\u5907\u7684\u6D77\u91CF\u6570\u636E\u5E76\u51CF\u8F7B\u7F51\u7EDC\u4F20\u8F93\u538B\u529B\uFF0C\u4EBA\u5DE5\u667A\u80FD\u7684\u7B97\u529B\u4F1A\u9010\u6E10\u5411\u8FB9\u7F18\u8F6C\u79FB\u3002\u90A3\u4EC0\u4E48\u662F\u8FB9\u7F18\u8BA1\u7B97\u90A3\uFF1F</p><p>\u8FB9\u7F18\u8BA1\u7B97\u6307\u7684\u662F\u5728\u7F51\u7EDC\u8FB9\u7F18\u7ED3\u70B9\u6765\u5904\u7406\u3001\u5206\u6790\u6570\u636E\u3002\u8FB9\u7F18\u7ED3\u70B9\u6307\u7684\u5C31\u662F\u5728\u6570\u636E\u4EA7\u751F\u6E90\u5934\u548C\u4E91\u4E2D\u5FC3\u4E4B\u95F4\u4EFB\u4E00\u5177\u6709\u8BA1\u7B97\u8D44\u6E90\u548C\u7F51\u7EDC\u8D44\u6E90\u7684\u7ED3\u70B9\u3002\u6BD4\u5982\u624B\u673A\u5C31\u53EF\u4EE5\u662F\u4EBA\u4E0E\u4E91\u4E2D\u5FC3\u4E4B\u95F4\u7684\u8FB9\u7F18\u8282\u70B9\uFF0C\u7F51\u5173\u662F\u667A\u80FD\u5BB6\u5C45\u548C\u4E91\u4E2D\u5FC3\u4E4B\u95F4\u7684\u8FB9\u7F18\u7ED3\u70B9\u3002</p><p>\u901A\u5E38\u6765\u8BF4\u8FB9\u7F18\u8BA1\u7B97\u628A\u66F4\u591A\u7684\u8BA1\u7B97\u8FDB\u7A0B\u653E\u5230\u8FB9\u7F18\u8282\u70B9\uFF0C\u4E91\u7AEF\u8FD0\u884C\u8F83\u5C11\u7684\u8FDB\u7A0B\u3002\u8FD9\u6837\uFF0C\u5C31\u53EF\u4EE5\u5B9E\u73B0\u5728\u6570\u636E\u4EA7\u751F\u6E90\u9644\u8FD1\u8BA1\u7B97\u3001\u5206\u6790\u6570\u636E\uFF0C\u6700\u5927\u9650\u5EA6\u51CF\u5C11\u5BA2\u6237\u7AEF\u4E0E\u4E91\u7AEF\u7684\u6570\u636E\u6D41\u8F6C\uFF0C\u8FDB\u800C\u51CF\u5C11\u4E91\u7AEF\u54CD\u5E94\u65F6\u95F4\u548C\u7F51\u7EDC\u670D\u52A1\u4E0D\u7A33\u5B9A\u6027\u3002</p><p>\u5982\u679C\u8FD8\u662F\u4E0D\u80FD\u7406\u89E3\u8FB9\u7F18\u8BA1\u7B97\uFF0C\u5C0F\u5305\u6765\u4E3E\u4E2A\u6817\u5B50: \u5927\u5BB6\u90FD\u542C\u8BF4\u8FC7\u7AE0\u9C7C\u5427</p><p>\u4E0D\u77E5\u9053\u5927\u5BB6\u4E86\u4E0D\u4E86\u89E3\u7AE0\u9C7C\uFF0C\u7AE0\u9C7C\u5177\u6709&quot;\u6982\u5FF5\u601D\u7EF4&quot;\uFF0C\u80FD\u591F\u72EC\u81EA\u89E3\u51B3\u590D\u6742\u7684\u95EE\u9898\uFF0C\u662F\u81EA\u7136\u754C\u4E2D\u667A\u5546\u6700\u9AD8\u7684\u65E0\u810A\u690E\u52A8\u7269\u3002\u8FD9\u4E0E\u5B83\u7684\u8BB0\u5FC6\u7CFB\u7EDF\u6709\u5173\uFF0C\u7AE0\u9C7C\u6709\u4E24\u4E2A\u8BB0\u5FC6\u7CFB\u7EDF\uFF0C\u4E00\u4E2A\u662F\u5927\u8111\uFF0C\u5177\u67095\u4EBF\u795E\u7ECF\u5143\uFF1B\u53E6\u5916\u4E00\u4E2A\u662F\u516B\u4E2A\u722A\u5B50\u4E0A\u7684\u5438\u76D8\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u7AE0\u9C7C\u7684\u516B\u4E2A\u722A\u53EF\u4EE5\u601D\u8003\u548C\u89E3\u51B3\u95EE\u9898\u3002</p><p>\u7AE0\u9C7C\u7684\u5927\u8111\u5C31\u7C7B\u4F3C\u4E8E\u4E91\u8BA1\u7B97\uFF0C\u516B\u4E2A\u5C0F\u722A\u5B50\u5C31\u662F\u8FB9\u7F18\u8BA1\u7B97\uFF0C\u6BCF\u4E2A\u722A\u5B50\u5C31\u662F\u4E00\u4E2A\u5C0F\u578B\u7684\u673A\u623F\u3002\u4E91\u8BA1\u7B97\u628A\u63E1\u6574\u4F53\uFF0C\u8FB9\u7F18\u8BA1\u7B97\u628A\u63E1\u5C40\u90E8\u3002</p><p>\u7531\u4E8E\u8FB9\u7F18\u8BA1\u7B97\u66F4\u9760\u8FD1\u6570\u636E\uFF0C\u56E0\u6B64\u8FB9\u7F18\u8BA1\u7B97\u4F1A\u5177\u5907\u4E0B\u5217\u4F18\u52BF:</p><ul><li>\u6570\u636E\u672C\u5730\u5316: \u6570\u636E\u65E0\u9700\u53D1\u5F80\u4E91\u7AEF\uFF0C\u89E3\u51B3\u4E91\u7AEF\u5B58\u50A8\u53CA\u9690\u79C1\u95EE\u9898\uFF1B</li><li>\u8BA1\u7B97\u672C\u5730\u5316: \u5728\u8FB9\u7F18\u8282\u70B9\u8FDB\u884C\u90E8\u5206\u8BA1\u7B97\uFF0C\u964D\u4F4E\u4E86\u4E91\u7AEF\u7684\u8BA1\u7B97\u538B\u529B\uFF0C\u89E3\u51B3\u4E91\u7AEF\u8BA1\u7B97\u8FC7\u8F7D\u95EE\u9898\uFF1B</li><li>\u4F4E\u901A\u4FE1\u6210\u672C: \u4E0D\u7528\u4E0E\u4E91\u7AEF\u8FDB\u884C\u4EA4\u4E92\uFF0C\u53EF\u4EE5\u5B9E\u73B0\u4F4E\u65F6\u5EF6\u548C\u5B9E\u65F6\u6027\u8F83\u5F3A\u7684\u573A\u666F\uFF0C\u540C\u65F6\u4E5F\u8282\u7701\u4E86\u901A\u4FE1\u6210\u672C\uFF1B</li><li>\u53BB\u4E2D\u5FC3\u5316\u8BA1\u7B97\uFF0C\u6545\u969C\u89C4\u907F\u4E0E\u6781\u81F4\u4E2A\u6027\u5316\u3002</li></ul><p>\u6211\u4EEC\u53EF\u4EE5\u4E3E\u51E0\u4E2A\u8FB9\u7F18\u8BA1\u7B97\u5178\u578B\u6848\u4F8B\u6765\u4F53\u9A8C\u8FB9\u7F18\u8BA1\u7B97\u7684\u4F18\u70B9:</p><ul><li>\u5728\u4EBA\u8138\u8BC6\u522B\u9886\u57DF\uFF0C\u8FB9\u7F18\u8BA1\u7B97\u53EF\u4EE5\u5C06\u54CD\u5E94\u65F6\u95F4\u7531 900ms \u51CF\u5C11\u4E3A169ms\uFF1B</li><li>\u628A\u90E8\u5206\u8BA1\u7B97\u4EFB\u52A1\u4ECE\u4E91\u7AEF\u5378\u8F7D\u5230\u8FB9\u7F18\u4E4B\u540E\uFF0C\u6574\u4E2A\u7CFB\u7EDF\u5BF9\u80FD\u6E90\u7684\u6D88\u8017\u80FD\u591F\u51CF\u5C11 30%-40% \uFF1B</li><li>\u7CFB\u7EDF\u6570\u636E\u5728\u6574\u5408\u3001\u8FC1\u79FB\u7B49\u65B9\u9762\u8017\u65F6\u5C06\u4F1A\u7F29\u51CF\u5230\u539F\u6765\u7684 1/20 \uFF1B</li></ul><p>\u4F46\u9700\u8981\u6CE8\u610F\uFF0C\u867D\u7136\u8FB9\u7F18\u8BA1\u7B97\u975E\u5E38\u725B\uFF0C\u4F46\u662F\u8FB9\u7F18\u8BA1\u7B97\u5E76\u975E\u662F\u4E91\u8BA1\u7B97\u7684&quot;\u53D6\u4EE3\u54C1&quot;\uFF0C\u4E0D\u80FD\u5C06\u4E91\u8BA1\u7B97\u4E00\u5200\u5207(\u867D\u8BF4\u7AE0\u9C7C\u722A\u5B50\u80FD\u601D\u8003\uFF0C\u4F46\u4E5F\u4E0D\u80FD\u628A\u5927\u8111\u5207\u6389\u5149\u9760\u722A\u5B50\u751F\u6D3B\u554A)\u3002\u5982\u679C\u8BF4\u4E91\u8BA1\u7B97\u66F4\u6CE8\u91CD\u628A\u63A7\u5168\u5C40\uFF0C\u90A3\u8FB9\u7F18\u8BA1\u7B97\u5219\u805A\u7126\u4E8E\u5C40\u90E8\uFF0C\u672C\u8D28\u4E0A\u662F\u5BF9\u4E91\u8BA1\u7B97\u7684\u8865\u5145\u548C\u4F18\u5316\uFF0C\u8FB9\u7F18\u8BA1\u7B97\u66F4\u9760\u8FD1\u6570\u636E\u6E90\u5934\uFF0C\u80FD\u4F4E\u65F6\u5EF6\u8FD1\u5B9E\u65F6\u7684\u89E3\u51B3\u95EE\u9898\u3002</p><p>IDC \u9884\u6D4B\uFF0C\u5230 2023 \u5E74\uFF0C\u5C06\u6709 20% \u7684\u8FB9\u7F18\u670D\u52A1\u5668\u7528\u6765\u5904\u7406 AI \u5DE5\u4F5C\u8D1F\u8F7D\uFF0C70% \u7684\u4F01\u4E1A\u5C06\u5728\u7269\u8054\u7F51\u8FB9\u7F18\u8FD0\u884C\u4E0D\u540C\u7EA7\u522B\u7684\u6570\u636E\u5904\u7406\u3002</p><p>\u8FB9\u7F18\u8BA1\u7B97\u540C\u6837\u4F1A\u5B58\u5728\u5F88\u591A\u6311\u6218:</p><ul><li>\u8FB9\u7F18\u8BA1\u7B97\u4E2D\u5FC3\u4E0E\u4E0D\u540C\u6570\u636E\u91C7\u96C6\u6D4B\u901A\u8BAF\u534F\u8BAE\u7684\u4E0D\u4E00\u81F4</li><li>\u8FB9\u7F18\u5E94\u7528\u573A\u666F\u788E\u7247\u5316\uFF0C\u65E0\u6CD5\u5F62\u6210\u89C4\u6A21\u5316\u573A\u666F\u3002</li><li>\u8FB9\u7F18\u8BA1\u7B97\u5177\u6709\u6F5C\u5728\u80FD\u91CF\u7684\u6570\u636E\u4E22\u5931: \u8FB9\u7F18\u8BA1\u7B97\u7684\u6570\u636E\u5F80\u5F80\u4E0D\u4F1A\u9001\u5F80\u4E91\u5B58\u50A8\uFF0C\u8FB9\u7F18\u8BA1\u7B97\u5904\u7406\u540E\u4F1A\u4E22\u5F03</li></ul><h2 id="\u7AEF\u667A\u80FD" tabindex="-1">\u7AEF\u667A\u80FD <a class="header-anchor" href="#\u7AEF\u667A\u80FD" aria-hidden="true">#</a></h2><p>\u4E0E\u4E91\u8BA1\u7B97\u5230\u8FB9\u7F18\u8BA1\u7B97\u7C7B\u4F3C\uFF0C\u4EBA\u5DE5\u667A\u80FD\u7684\u53D1\u5C55\u4E5F\u6B63\u5728\u7ECF\u5386\u4ECE\u4E91\u5230\u7AEF\u7684\u8FC7\u7A0B\u3002 \u6211\u4EEC\u56DE\u60F3\u4E00\u4E2A\u4EBA\u5DE5\u667A\u80FD\u5E94\u7528\u7684\u5F00\u53D1\u6D41\u7A0B\uFF0C\u4E00\u822C\u6709\u4E0B\u5217\u56DB\u6B65:</p><ul><li>\u6570\u636E\u7684\u91C7\u96C6\u4E0E\u9884\u5904\u7406</li><li>\u6A21\u578B\u7684\u9009\u53D6\u4E0E\u8BAD\u7EC3</li><li>\u6A21\u578B\u6548\u679C\u8BC4\u4F30</li><li>\u6A21\u578B\u7684\u670D\u52A1\u90E8\u7F72\u548C\u63A8\u7406</li></ul><p>\u6A21\u578B\u63A8\u7406\u7684\u65B9\u6848\u4E00\u822C\u662F\u90E8\u7F72\u5728\u4E91\u7AEF\u6216\u8005\u670D\u52A1\u5668\u7AEF\uFF0C\u5BF9\u5916\u63D0\u4F9B API \u7ED9\u5BA2\u6237\u7AEF\u3002\u8FD9\u79CD\u6A21\u5F0F\u5B58\u5728\u5F88\u591A\u4F18\u52BF\uFF0C\u4E91\u670D\u52A1\u5668\u7A7A\u95F4\u8D44\u6E90\u5145\u8DB3\uFF0C\u53EF\u4EE5\u5B58\u50A8\u6D77\u91CF\u6570\u636E\uFF1B\u7B97\u529B\u5F3A\u5927\uFF0C\u80FD\u8FDB\u884C\u590D\u6742\u7684\u6A21\u578B\u63A8\u7406\u3002\u4F46\u4E0D\u514D\u51FA\u73B0\u7C7B\u4F3C\u4E91\u8BA1\u7B97\u7684\u95EE\u9898:</p><ul><li>\u54CD\u5E94\u901F\u5EA6: \u6570\u636E\u7684\u4F20\u8F93\u4E0E\u54CD\u5E94\u4F9D\u9760\u7F51\u7EDC\u4F20\u8F93\uFF0C\u7A33\u5B9A\u6027\u548C\u54CD\u5E94\u901F\u5EA6\u65E0\u6CD5\u4FDD\u6301\u7A33\u5B9A\uFF0C\u5BF9\u4E8E\u5B9E\u65F6\u95EE\u9898\u6216\u4F4E\u65F6\u5EF6\u95EE\u9898\u51E0\u4E4E\u65E0\u89E3\u3002\u6BD4\u5982\u65E0\u4EBA\u9A7E\u9A76\u6C7D\u8F66\uFF0C\u9A7E\u9A76\u8FC7\u7A0B\u4E2D\u9047\u5230\u4E86\u5341\u5B57\u8DEF\u53E3\uFF0C\u4F46\u6B64\u65F6\u6070\u597D\u51FA\u73B0\u4E86\u7F51\u7EDC\u6CE2\u52A8\uFF0C\u8054\u7CFB\u4E0D\u5230\u4E91\u7AEF\uFF0C\u90A3\u8BE5\u5411\u5DE6\u8FD8\u662F\u5411\u53F3\u90A3\uFF1F</li><li>\u6570\u636E\u9690\u79C1\u6027: 19 \u5E74\u5DE5\u4FE1\u90E8\u53D1\u51FA\u300A\u5173\u4E8E\u5F00\u5C55APP\u4FB5\u5BB3\u7528\u6237\u6743\u76CA\u4E13\u9879\u6574\u6CBB\u5DE5\u4F5C\u7684\u901A\u77E5\u300B\uFF0C\u8BF4\u660E\u6211\u56FD\u5BF9\u4E2A\u4EBA\u6570\u636E\u9690\u79C1\u6027\u7684\u91CD\u89C6\u5728\u52A0\u5F3A\uFF0C\u5E94\u7528\u60F3\u8981\u83B7\u5F97\u5927\u91CF\u6570\u636E\u53D1\u5F80\u4E91\u7AEF\u53D8\u5F97\u56F0\u96BE\uFF0C\u6CA1\u6709\u6570\u636E\u7684\u4E91\u7AEF\u5C31\u597D\u6BD4&quot;\u5DE7\u5987\u96BE\u4E3A\u65E0\u7C73\u4E4B\u708A&quot;\u3002</li></ul><p>\u7AEF\u667A\u80FD\u7B80\u5355\u6765\u8BF4\u5C31\u662F\u5728\u7AEF\u4FA7\u5B8C\u6210\u6A21\u578B\u63A8\u7406\u8FC7\u7A0B\u3002\u76F8\u6BD4\u4E8E\u4E91\u667A\u80FD\u6765\u8BF4\uFF0C\u7AEF\u667A\u80FD\u6709\u7740\u4EE5\u4E0B\u51E0\u70B9\u663E\u8457\u4F18\u52BF:</p><ul><li>\u4F4E\u5EF6\u65F6: \u6A21\u578B\u63A8\u7406\u5728\u7AEF\u4E0A\u8FDB\u884C\uFF0C\u65E0\u9700\u4E0E\u4E91\u7AEF\u8FDB\u884C\u7F51\u7EDC\u8BF7\u6C42\uFF0C\u964D\u4F4E\u4E86\u54CD\u5E94\u65F6\u95F4\u3002\u4F4E\u5EF6\u65F6\u5BF9\u4E8E\u4E00\u4E9B\u5B9E\u65F6\u6027\u8981\u6C42\u9AD8\u7684\u573A\u666F\u6781\u4E3A\u91CD\u8981\u3002</li><li>\u5B89\u5168\u6027: \u6570\u636E\u65E0\u9700\u4F20\u5F80\u4E91\u7AEF\uFF0C\u53EF\u4EE5\u66F4\u597D\u5730\u4FDD\u62A4\u7528\u6237\u9690\u79C1\u6570\u636E\u3002</li><li>\u5B9A\u5236\u5316: \u6839\u636E\u7528\u6237\u4E60\u60EF\u8FDB\u884C\u672C\u5730\u8BAD\u7EC3\uFF0C\u6B65\u6B65\u4F18\u5316\uFF0C\u53EF\u4EE5\u66F4\u597D\u7684\u5B9E\u73B0&quot;\u5343\u4EBA\u5343\u9762&quot;</li><li>\u8282\u7701\u8D44\u6E90: \u5728\u7AEF\u4FA7\u5904\u7406\uFF0C\u5229\u7528\u7AEF\u4FA7\u7B97\u529B\u548C\u5B58\u50A8\u7A7A\u95F4\uFF0C\u53EF\u4EE5\u8282\u7701\u5927\u91CF\u7684\u4E91\u7AEF\u8BA1\u7B97\u548C\u5B58\u50A8\u8D44\u6E90</li></ul><p>\u4F46\u7AEF\u667A\u80FD\u4E5F\u5B58\u5728\u6BD4\u8F83\u81F4\u547D\u7684\u7F3A\u9677\u2014\u2014\u4F4E\u7B97\u529B\u3002\u7AEF\u4FA7\u7684\u7B97\u529B\u548C\u5B58\u50A8\u4E0E\u4E91\u670D\u52A1\u5668\u76F8\u6BD4\u8FD8\u76F8\u5DEE\u751A\u8FDC\uFF0C\u65E0\u6CD5\u505A\u5927\u89C4\u6A21\u9AD8\u5F3A\u5EA6\u7684\u8BA1\u7B97\u3002</p><p>\u4E3A\u4E86\u5C3D\u53EF\u80FD\u538B\u69A8\u7B97\u529B\uFF0C\u5C31\u9700\u8981\u5BF9\u786C\u4EF6\u5E73\u53F0\u8FDB\u884C\u9002\u914D\uFF0C\u505A\u6307\u4EE4\u7EA7\u7684\u4F18\u5316\uFF1B\u540C\u65F6\u538B\u7F29\u6A21\u578B\uFF0C\u5728\u65F6\u95F4\u548C\u7A7A\u95F4\u4E0A\u51CF\u5C11\u6D88\u8017\u3002</p><p>\u540C\u65F6\uFF0C\u7531\u4E8E\u7AEF\u4FA7\u53EA\u662F\u5355\u7528\u6237\u6570\u636E\uFF0C\u6570\u636E\u91CF\u5C0F\uFF0C\u5728\u7B97\u6CD5\u4E0A\u65E0\u6CD5\u505A\u5230\u6700\u4F18\u3002\u7528\u6237\u6570\u636E\u6709\u9650\uFF0C\u7AEF\u4FA7\u6570\u636E\u5E76\u4E0D\u9002\u5408\u957F\u671F\u5927\u91CF\u5B58\u50A8\uFF0C\u7AEF\u4FA7\u53EF\u7528\u6570\u636E\u6709\u9650\u3002</p><h3 id="\u7AEF\u667A\u80FD\u5E94\u7528" tabindex="-1">\u7AEF\u667A\u80FD\u5E94\u7528 <a class="header-anchor" href="#\u7AEF\u667A\u80FD\u5E94\u7528" aria-hidden="true">#</a></h3><p>\u8FD1\u5E74\u6765\uFF0C\u7AEF\u667A\u80FD\u7684\u5E94\u7528\u573A\u666F\u8D8A\u6765\u8D8A\u591A\uFF0C\u5E7F\u6CDB\u5E94\u7528\u4E8E\u4EBA\u8138\u8BC6\u522B\u3001\u624B\u52BF\u8BC6\u522B\u3001\u56FE\u50CF\u641C\u7D22\u3001\u4E92\u52A8\u6E38\u620F\u7B49\uFF0C\u4F46\u53EF\u4EE5\u7C97\u7565\u7684\u5206\u6210\u4E24\u7C7B:</p><ul><li>\u5229\u7528 AI\uFF0C\u521B\u5EFA\u65B0\u7684\u4EA4\u4E92\u65B9\u5F0F: \u624B\u6DD8\u7F8E\u5986 AR : \u57FA\u4E8E\u7AEF\u4FA7 AI \u7684\u2F08\u8138\u68C0\u6D4B\u80FD\u2F12\u7ED3\u5408 AR \u5E94\u2F64\u53EF\u4EE5\u7ED9\u6D88\u8D39\u8005\u66F4\u52A0\u771F\u5B9E\u7684\u8D2D\u7269\u4F53\u9A8C\u3002\u652F\u4ED8\u5B9D\u626B\u798F;\u98DE\u732A\u53CC\u5341\u4E00\u4E92\u52A8\u6E38\u620F&quot;\u627E\u4E00\u627E&quot;;AR\u5E94\u7528\u3001\u6E38\u620F: \u7F8E\u989C\u76F8\u673A\u3001\u865A\u62DF\u8BD5\u5986</li><li>\u5229\u7528 AI \u65B0\u6280\u672F\uFF0C\u5B9E\u73B0\u66F4\u4E2A\u6027\u5316\uFF0C\u65F6\u6548\u6027\u7B49\u7684\u5DE5\u4F5C\u6D41\u7A0B <ul><li>\u7AEF\u4FA7\u91CD\u6392: \u901A\u8FC7\u5B9E\u65F6\u8BC6\u522B\u7528\u6237\u610F\u8BC6\uFF0C\u91CD\u65B0\u6392\u5217\u670D\u52A1\u5668\u63A8\u8350\u7B97\u6CD5\u751F\u6210\u7684 feeds \u6D41\uFF0C\u4F7F\u5185\u5BB9\u63A8\u8350\u66F4\u7CBE\u51C6\u3002</li><li>\u4E2A\u6027\u5316: \u624B\u673A\u6DD8\u5B9D&quot;\u731C\u4F60\u559C\u6B22&quot;\u9875\u9762</li></ul></li></ul><h3 id="\u7AEF\u667A\u80FD\u6311\u6218" tabindex="-1">\u7AEF\u667A\u80FD\u6311\u6218 <a class="header-anchor" href="#\u7AEF\u667A\u80FD\u6311\u6218" aria-hidden="true">#</a></h3><p>\u7AEF\u667A\u80FD\u7684\u5E94\u7528\u5DF2\u7ECF\u975E\u5E38\u5E7F\u6CDB\u548C\u666E\u904D\uFF0C\u4F46\u7AEF\u667A\u80FD\u7684\u53D1\u5C55\u4E5F\u9762\u4E34\u7740\u4E00\u4E9B\u6311\u6218\u3002</p><ul><li>\u8BBE\u5907\u7684\u788E\u7247\u5316: \u7AEF\u4FA7\u8BBE\u5907\u4E25\u91CD\u788E\u7247\u5316\uFF0C\u590D\u6742\u591A\u6837\u7684\u64CD\u4F5C\u7CFB\u7EDF\u548C\u7CFB\u7EDF\u7248\u672C\u3002\u5982\u4F55\u4FDD\u8BC1\u6A21\u578B\u9002\u914D\u5404\u79CD\u8BBE\u5907\u4E14\u5145\u5206\u5229\u7528\u52A0\u901F\uFF0C\u662F\u7AEF\u667A\u80FD\u53D1\u5C55\u7684\u6311\u6218\u4E4B\u4E00\u3002</li><li>\u6A21\u578B\u4E0E\u5F15\u64CE\u5927\u5C0F: \u6A21\u578B\u592A\u5927\u4F1A\u5F71\u54CD\u52A0\u8F7D\u901F\u5EA6\uFF0C\u8FD0\u884C\u65F6\u4E5F\u4F1A\u5360\u7528\u5DE8\u91CF\u5185\u5B58\u3002\u63A8\u7406\u5F15\u64CE\u9700\u8981\u96C6\u6210\u5230app\u4E2D\uFF0Capp\u592A\u5927\u4E5F\u4F1A\u5360\u636E\u5927\u91CF\u5B58\u50A8\u3002</li><li>\u5185\u5B58\u5360\u7528: \u8FD0\u884C\u65F6\u4F1A\u5360\u7528\u5927\u91CF\u5185\u5B58\uFF0C\u4F1A\u5F71\u54CD\u7528\u6237\u4F53\u9A8C\u3002</li></ul><h3 id="\u7AEF\u667A\u80FD\u8D8B\u52BF" tabindex="-1">\u7AEF\u667A\u80FD\u8D8B\u52BF <a class="header-anchor" href="#\u7AEF\u667A\u80FD\u8D8B\u52BF" aria-hidden="true">#</a></h3><p>\u603B\u7ED3\u51FA\u51E0\u5927\u8D8B\u52BF: \u7AEF\u4E0A\u63A8\u7406\u4E0E\u8BAD\u7EC3\u7AEF\u4E0A\u7684\u63A8\u7406\u9AD8\u4E8E\u8BAD\u7EC3\uFF0C\u4F46\u4ECD\u9700\u8981\u8865\u9F50\u7AEF\u4E0A\u7684\u8BAD\u7EC3\u80FD\u529B\u3002</p><p>IDC\u9884\u6D4B\uFF0C\u7528\u4E8E\u63A8\u7406\u5DE5\u4F5C\u8D1F\u8F7D\u7684\u670D\u52A1\u5668\u5E02\u573A\u4EFD\u989D\u5C06\u5728\u4E0D\u4E45\u7684\u5C06\u6765\u8D85\u8FC7\u8BAD\u7EC3\uFF0C\u5E76\u5728\u9884\u6D4B\u671F\u5185\u4FDD\u6301\u8FD9\u4E00\u8D8B\u52BF\u3002</p><h3 id="\u540E\u6469\u5C14\u65F6\u4EE3\u4E0Expu" tabindex="-1">\u540E\u6469\u5C14\u65F6\u4EE3\u4E0EXPU <a class="header-anchor" href="#\u540E\u6469\u5C14\u65F6\u4EE3\u4E0Expu" aria-hidden="true">#</a></h3><p>\u6469\u5C14\u5B9A\u5F8B\u5728\u8FC7\u53BB\u7684\u51E0\u5E74\u9010\u6E10\u5931\u6548\uFF0Cgeneral-purpose CPU \u6027\u80FD\u7684\u589E\u957F\u901F\u5EA6\u5DF2\u7ECF\u5E73\u6ED1\u3002\u4F46\u4EBA\u5DE5\u667A\u80FD\u6A21\u578B\u5BF9\u7B97\u529B\u7684\u8981\u6C42\u8D8A\u6765\u8D8A\u9AD8\u3002\u56E0\u6B64\u5F88\u591A\u516C\u53F8\u63A8\u51FA\u4E86\u9488\u5BF9 AI \u7279\u6B8A\u52A0\u901F\u7684 &quot;XPU&quot;\u3002</p><p>\u672A\u6765\u7AEF\u667A\u80FD\u7B97\u529B\u4E3B\u8981\u9760\u5404\u5F0F\u5404\u6837\u7684 XPU \u6765\u63D0\u4F9B\u3002\u5982\u4F55\u9002\u914D\u788E\u7247\u5316\u7684 XPU \uFF0C\u5145\u5206\u5229\u7528\u786C\u4EF6\u80FD\u529B\uFF0C\u662F\u672A\u6765\u63A8\u7406\u5F15\u64CE\u8981\u91CD\u70B9\u7A81\u7834\u7684\u9886\u57DF\u3002</p><h3 id="\u7531\u624B\u673A\u7AEF\u5230aiot\u7AEF" tabindex="-1">\u7531\u624B\u673A\u7AEF\u5230AIOT\u7AEF <a class="header-anchor" href="#\u7531\u624B\u673A\u7AEF\u5230aiot\u7AEF" aria-hidden="true">#</a></h3><p>\u672A\u6765\u51E0\u5E74\uFF0C\u5168\u7403\u7684\u624B\u673A\u51FA\u8D27\u91CF\u4E0D\u4F1A\u50CF\u5F80\u5E74\u4E00\u6837\u5927\u5E45\u589E\u957F\uFF0C\u800C\u662F\u5E73\u7A33\u751A\u81F3\u4E0B\u6ED1(\u89C1\u4E0B\u56FE\u5168\u7403\u624B\u673A\u51FA\u8D27\u91CF\u8D8B\u52BF)\uFF0C\u4F46 AIOT \u7AEF\u4EA7\u54C1\u4E00\u65B9\u9762\u79CD\u7C7B\u591A\u6837\uFF0C\u6BD4\u5982\u50CF\u5B89\u9632\u9886\u57DF\u7684\u6307\u7EB9\u9501\u3001\u76D1\u63A7\u6444\u50CF\u5934\u3001\u65E0\u4EBA\u673A\u7B49\u7B49\u3002</p><p>\u53E6\u4E00\u65B9\u9762\u968F\u7740\u4E91\u8BA1\u7B97\u7684\u53D1\u5C55\uFF0C\u7269\u8054\u7F51\u754C\u6B63\u8FDB\u884C\u7740\u8DE8\u8D8A\u5F0F\u7684\u53D1\u5C55\uFF0CAIOT \u7AEF\u6570\u91CF\u4F1A\u8D8A\u6765\u8D8A\u591A</p><h3 id="\u7AEF\u667A\u80FD\u6846\u67B6\u53D1\u5C55" tabindex="-1">\u7AEF\u667A\u80FD\u6846\u67B6\u53D1\u5C55 <a class="header-anchor" href="#\u7AEF\u667A\u80FD\u6846\u67B6\u53D1\u5C55" aria-hidden="true">#</a></h3><p>\u7ECF\u8FC7\u8FD1\u51E0\u5E74\u7684\u53D1\u5C55\uFF0C\u5DF2\u7ECF\u6709\u4E00\u4E9B\u6BD4\u8F83\u6210\u719F\u7684\u7AEF\u4FA7\u63A8\u7406\u5F15\u64CE:</p><ul><li>Google/TensorFlow Lite</li><li>Facebook/PyTorch</li><li>\u817E\u8BAF/NCNN</li><li>\u5B57\u8282/Pitaya</li><li>\u767E\u5EA6/Paddle-Lite</li><li>\u6DD8\u5B9D/MNN</li></ul><h2 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h2><ul><li><a href="https://juejin.cn/post/6959366414666858526" target="_blank" rel="noopener noreferrer">\u4E00\u952E\u96C6\u6210\u7684\u7AEF\u667A\u80FD\u6846\u67B6Pitaya</a></li><li><a href="https://juejin.cn/post/6844904071925596173" target="_blank" rel="noopener noreferrer">\u4E00\u6587\u641E\u61C2\u4EC0\u4E48\u662F\u8FB9\u7F18\u8BA1\u7B97</a></li><li><a href="https://juejin.cn/post/6987217559242145800" target="_blank" rel="noopener noreferrer">\u6CA1\u60F3\u5230\uFF0C\u4F60\u662F\u8FD9\u6837\u7684\u7AEF\u667A\u80FD</a></li><li><a href="https://juejin.cn/post/7013674501116264484" target="_blank" rel="noopener noreferrer">\u8BF4\u4E00\u8BF4Web\u7AEF\u4FA7AI</a></li><li><a href="https://blog.csdn.net/LEON1741/article/details/105937527" target="_blank" rel="noopener noreferrer">\u4E09\u5206\u949F\u770B\u61C2\u4EC0\u4E48\u662F\u8FB9\u7F18\u8BA1\u7B97\u548C\u4E91\u8BA1\u7B97</a></li></ul>',53),p=[r];function n(o,h,d,u,s,c){return i(),l("div",null,p)}var g=e(t,[["render",n]]);export{f as __pageData,g as default};
