import{_ as e,c as a,o as t,a as r}from"./app.d337cdfc.js";const b='{"title":"\u5982\u4F55\u81EA\u5B66\u56FE\u5F62\u5B66","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u56FE\u5F62\u57FA\u7840","slug":"\u56FE\u5F62\u57FA\u7840"},{"level":3,"title":"\u4F20\u7EDF\u7684html + css","slug":"\u4F20\u7EDF\u7684html-css"},{"level":3,"title":"SVG","slug":"svg"},{"level":3,"title":"canvas2D","slug":"canvas2d"},{"level":3,"title":"WebGL","slug":"webgl"},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3"}],"relativePath":"tech/graphic/plan.md","lastUpdated":1654763687134}',l={},i=r('<h1 id="\u5982\u4F55\u81EA\u5B66\u56FE\u5F62\u5B66" tabindex="-1">\u5982\u4F55\u81EA\u5B66\u56FE\u5F62\u5B66 <a class="header-anchor" href="#\u5982\u4F55\u81EA\u5B66\u56FE\u5F62\u5B66" aria-hidden="true">#</a></h1><p>\u56FE\u5F62\u5B66\u8FD9\u4E2A\u9886\u57DF\u76EE\u524D\u6765\u770B\u662F\u5F88\u597D\u73A9\u4E5F\u5F88\u6709\u524D\u666F\u7684\u4E00\u4E2A\u65B9\u5411\uFF0C\u5F53\u6211\u4EEC\u4E86\u89E3\u5B83\u7684\u57FA\u7840\u77E5\u8BC6\uFF0Cget\u5230\u5B83\u597D\u73A9\u5730\u65B9\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F88\u8F7B\u677E\u5EF6\u4F38\u5230\u53EF\u89C6\u5316\u8FD9\u4E00\u9886\u57DF\u8FDB\u884C\u62D3\u5C55\u3002 \u8DDF\u6708\u5F71\u5B66\u53EF\u89C6\u5316\uFF1A<a href="https://time.geekbang.org/column/intro/100053801" target="_blank" rel="noopener noreferrer">https://time.geekbang.org/column/intro/100053801</a></p><p>\u8BA1\u7B97\u673A\u56FE\u5F62\u5B66\u662F\u4E00\u95E8\u7EFC\u5408\u591A\u5B66\u79D1\u7684\u6D3B\u8DC3\u7684\u7814\u7A76\u6027\u5B66\u79D1\uFF0C\u4E0D\u662F\u6734\u7D20\u7684\u5DE5\u7A0B\u5B9E\u8DF5\u3002</p><p>\u8BA1\u7B97\u673A\u56FE\u5F62\u5B66\u53EF\u4EE5\u7B3C\u7EDF\u7684\u5206\u4E3A\u4E09\u4E2A\u5927\u65B9\u5411\uFF0C\u6E32\u67D3rendering, \u51E0\u4F55geometry\u548C\u6A21\u62DFsimulation\u3002</p><h2 id="\u56FE\u5F62\u57FA\u7840" tabindex="-1">\u56FE\u5F62\u57FA\u7840 <a class="header-anchor" href="#\u56FE\u5F62\u57FA\u7840" aria-hidden="true">#</a></h2><p>\u5728 Web \u4E0A\uFF0C\u56FE\u5F62\u901A\u5E38\u662F\u901A\u8FC7\u6D4F\u89C8\u5668\u7ED8\u5236\u7684\u3002\u73B0\u4EE3\u6D4F\u89C8\u5668\u662F\u4E00\u4E2A\u590D\u6742\u7684\u7CFB\u7EDF\uFF0C\u5176\u4E2D\u8D1F\u8D23\u7ED8\u5236\u56FE\u5F62\u7684\u90E8\u5206\u662F\u6E32\u67D3\u5F15\u64CE\u3002\u6E32\u67D3\u5F15\u64CE\u7ED8\u5236\u56FE\u5F62\u7684\u65B9\u5F0F\uFF0C\u4E00\u822C\u5927\u4F53\u4E0A\u6709 4 \u79CD\u3002</p><h3 id="\u4F20\u7EDF\u7684html-css" tabindex="-1">\u4F20\u7EDF\u7684html + css <a class="header-anchor" href="#\u4F20\u7EDF\u7684html-css" aria-hidden="true">#</a></h3><p>\u200B\u4E0E\u4F20\u7EDF\u7684 Web \u5E94\u7528\u76F8\u6BD4\uFF0C\u53EF\u89C6\u5316\u9879\u76EE\uFF0C\u5C24\u5176\u662F PC \u7AEF\u7684\u53EF\u89C6\u5316\u5927\u5C4F\u5C55\u73B0\uFF0C\u4F7F\u7528HTML \u4E0E CSS \u60C5\u666F\u76F8\u5BF9\u8F83\u5C11\uFF0C\u4E8E\u662F\u53EF\u80FD\u6709\u4E9B\u4EBA\u4F1A\u8BEF\u8BA4\u4E3A\uFF0C\u53EF\u89C6\u5316\u53EA\u80FD\u4F7F\u7528 SVG\u3001Canvas \u8FD9\u4E9B\u65B9\u5F0F\uFF0C\u4E0D\u80FD\u4F7F\u7528 HTML \u4E0E CSS\u3002\u5F53\u7136\u4E86\uFF0C\u8FD9\u4E2A\u60F3\u6CD5\u662F\u4E0D\u5BF9\u3002 \u200B \u5176\u5B9E\u73B0\u4EE3\u6D4F\u89C8\u5668\u7684HTML\u3001CSS \u8868\u73B0\u80FD\u529B\u5F88\u5F3A\u5927,\u5176\u5B9E\u4E00\u4E9B\u7B80\u5355\u7684\u53EF\u89C6\u5316\u56FE\u8868\uFF0C\u5B8C\u5168\u53EF\u4EE5\u7528 CSS \u6765\u5B9E\u73B0\uFF0C\u6BD4\u5982\uFF0C\u6211\u4EEC\u5E38\u89C1\u7684\u67F1\u72B6\u56FE\u3001\u997C\u56FE\u548C\u6298\u7EBF\u56FE\u3002\u80FD\u7B80\u5316\u5F00\u53D1\uFF0C\u53C8\u4E0D\u9700\u8981\u5F15\u5165\u989D\u5916\u7684\u5E93\uFF0C\u53EF\u4EE5\u8282\u7701\u8D44\u6E90\uFF0C\u63D0\u9AD8\u7F51\u9875\u6253\u5F00\u7684\u901F\u5EA6\u3002</p><p>\u4F46\u662F\u4F7F\u7528HTML + CSS\u4E5F\u662F\u6709\u4E00\u5B9A\u5F97\u5F0A\u7AEF\uFF1A 1\uFF09\u7EF4\u62A4\u9EBB\u70E6\uFF0C\u5728 CSS \u4EE3\u7801\u91CC\uFF0C\u6211\u4EEC\u5F88\u96BE\u770B\u51FA\u6570\u636E\u4E0E\u56FE\u5F62\u7684\u5BF9\u5E94\u5173\u7CFB\uFF0C\u6709\u5F88\u591A\u6362\u7B97\u4E5F\u9700\u8981\u5F00\u53D1\u4EBA\u5458\u81EA\u5DF1\u6765\u505A\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u4E00\u65E6\u56FE\u8868\u6216\u6570\u636E\u53D1\u751F\u6539\u52A8\uFF0C\u5C31\u9700\u8981\u6211\u4EEC\u91CD\u65B0\u8BA1\u7B97\uFF0C\u6240\u4EE5\u7EF4\u62A4\u8D77\u6765\u4F1A\u5F88\u9EBB\u70E6\u3002</p><p>\u6027\u80FD\u5F00\u9500\u662F\u975E\u5E38\u5927\uFF0CHTML \u548C CSS \u4F5C\u4E3A\u6D4F\u89C8\u5668\u6E32\u67D3\u5F15\u64CE\u7684\u4E00\u90E8\u5206\uFF0C\u4E3A\u4E86\u5B8C\u6210\u9875\u9762\u6E32\u67D3\u7684\u5DE5\u4F5C\uFF0C\u9664\u4E86\u7ED8\u5236\u56FE\u5F62\u5916\uFF0C\u8FD8\u8981\u505A\u5F88\u591A\u989D\u5916\u7684\u5DE5\u4F5C\u3002\u6BD4\u5982\u8BF4\uFF0C\u6D4F\u89C8\u5668\u7684\u6E32\u67D3\u5F15\u64CE\u5728\u5DE5\u4F5C\u65F6\uFF0C\u8981\u5148\u89E3\u6790 HTML\u3001SVG\u3001CSS\uFF0C\u6784\u5EFA DOM \u6811\u3001RenderObject \u6811\u548C RenderLayer \u6811\uFF0C\u7136\u540E\u7528 HTML\uFF08\u6216 SVG\uFF09\u7ED8\u56FE\u3002\u5F53\u56FE\u5F62\u53D1\u751F\u53D8\u5316\u65F6\uFF0C\u6211\u4EEC\u5F88\u53EF\u80FD\u8981\u91CD\u65B0\u6267\u884C\u5168\u90E8\u7684\u5DE5\u4F5C\u3002</p><p>\u90A3\u6709\u6CA1\u6709\u66F4\u597D\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF0C\u5F53\u6211\u4EEC\u5728\u91CD\u7ED8\u56FE\u50CF\u65F6\uFF0C\u4E0D\u4F1A\u53D1\u751F\u91CD\u65B0\u89E3\u6790\u6587\u6863\u548C\u6784\u5EFA\u7ED3\u6784\u7684\u8FC7\u7A0B\uFF0C\u8FD9\u4E2A\u5F53\u7136\u662F\u6709\u7684\uFF0C\u90A3\u540E\u9762\u4E5F\u4F1A\u4ECB\u7ECD\u5230\u3002</p><h3 id="svg" tabindex="-1">SVG <a class="header-anchor" href="#svg" aria-hidden="true">#</a></h3><p>SVG \u5B83\u662F\u6D4F\u89C8\u5668\u652F\u6301\u7684\u4E00\u79CD\u57FA\u4E8E XML \u8BED\u6CD5\u7684\u56FE\u50CF\u683C\u5F0F\uFF0C\u5B83\u7684 XML \u8BED\u8A00\u672C\u8EAB\u548C HTML \u975E\u5E38\u63A5\u8FD1\uFF0C\u90FD\u662F\u7531\u6807\u7B7E + \u5C5E\u6027\u6784\u6210\u7684\uFF0C\u800C\u4E14\u6D4F\u89C8\u5668\u7684 CSS\u3001JavaScript \u90FD\u80FD\u591F\u6B63\u5E38\u4F5C\u7528\u4E8E SVG \u5143\u7D20\u3002</p><h3 id="canvas2d" tabindex="-1">canvas2D <a class="header-anchor" href="#canvas2d" aria-hidden="true">#</a></h3><p>\u63A5\u4E0B\u6765\u5230\u4E86\u56FE\u5F62\u57FA\u7840\u7684\u91CD\u70B9\uFF0Ccanvas2D,\u540E\u7EED\u7684\u6570\u5B66\u57FA\u7840\u90E8\u5206\u4E5F\u662F\u5927\u591A\u6570\u4EE5\u5B83\u4E3A\u57FA\u7840\u8FDB\u884C\u7ED8\u5236\u3002</p><p>\u8FD9\u91CC\u8BF4\u4E00\u4E0B\u5B83\u7684\u58F0\u660E\u5F0F\u7ED8\u56FE\u7CFB\u7EDF\u548C\u6307\u4EE4\u5F0F\u7ED8\u56FE\u7CFB\u7EDF\u533A\u522B:</p><ul><li>\u58F0\u660E\u5F0F\u7ED8\u56FE\u7CFB\u7EDF\uFF1A\u6211\u4EEC\u6839\u636E\u6570\u636E\u521B\u5EFA\u5404\u79CD\u4E0D\u540C\u7684\u56FE\u5F62\u5143\u7D20\uFF08\u6216\u8005 CSS \u89C4\u5219\uFF09\uFF0C\u7136\u540E\u5229\u7528\u6D4F\u89C8\u5668\u6E32\u67D3\u5F15\u64CE\u89E3\u6790\u5B83\u4EEC\u5E76\u6E32\u67D3\u51FA\u6765\u3002</li><li>\u6307\u4EE4\u5F0F\u7ED8\u56FE\u7CFB\u7EDF\uFF1A\u5B83\u66F4\u591A\u7684\u662F\u6D4F\u89C8\u5668\u63D0\u4F9B\u7684\u4E00\u79CD\u53EF\u4EE5\u76F4\u63A5\u7528\u4EE3\u7801\u5728\u4E00\u5757\u5E73\u9762\u7684\u753B\u5E03\u4E0A\u7ED8\u5236\u56FE\u5F62\u7684api\uFF0C\u4F7F\u7528\u5B83\u6765\u7ED8\u56FE\u66F4\u50CF\u662F\u4F20\u7EDF\u7684\u201C\u7F16\u5199\u4EE3\u7801\u201D\uFF0C\u7B80\u5355\u6765\u8BF4\u5C31\u662F\u8C03\u7528\u7ED8\u56FE\u6307\u4EE4\uFF0C\u7136\u540E\u5F15\u64CE\u76F4\u63A5\u5728\u9875\u9762\u4E0A\u7ED8\u5236\u56FE\u5F62\u3002</li></ul><p>\u603B\u7ED3\u6765\u8BF4\uFF0C\u50CFCanvas \u80FD\u591F\u76F4\u63A5\u64CD\u4F5C\u7ED8\u56FE\u4E0A\u4E0B\u6587\uFF0C\u4E0D\u9700\u8981\u7ECF\u8FC7 HTML\u3001CSS \u89E3\u6790\u3001\u6784\u5EFA\u6E32\u67D3\u6811\u3001\u5E03\u5C40\u7B49\u4E00\u7CFB\u5217\u64CD\u4F5C\u3002\u56E0\u6B64\u5355\u7EAF\u7ED8\u56FE\u7684\u8BDD\uFF0CCanvas \u6BD4 HTML/CSS \u548C SVG \u8981\u5FEB\u5F97\u591A\u3001\u5728\u91CD\u7ED8\u56FE\u50CF\u65F6\uFF0C\u4E5F\u4E0D\u4F1A\u53D1\u751F\u91CD\u65B0\u89E3\u6790\u6587\u6863\u548C\u6784\u5EFA\u7ED3\u6784\u7684\u8FC7\u7A0B\uFF0C\u5F00\u9500\u8981\u5C0F\u5F88\u591A\u3002</p><h3 id="webgl" tabindex="-1">WebGL <a class="header-anchor" href="#webgl" aria-hidden="true">#</a></h3><p>webGL\u7684\u4F7F\u7528\u573A\u666F: \u200B- \u7B2C\u4E00\u79CD\u60C5\u51B5\uFF0C\u5982\u679C\u6211\u4EEC\u8981\u7ED8\u5236\u7684\u56FE\u5F62\u6570\u91CF\u975E\u5E38\u591A\uFF0C\u6BD4\u5982\u6709\u591A\u8FBE\u6570\u4E07\u4E2A\u51E0\u4F55\u56FE\u5F62\u9700\u8981\u7ED8\u5236\uFF0C\u800C\u4E14\u5B83\u4EEC\u7684\u4F4D\u7F6E\u548C\u65B9\u5411\u90FD\u5728\u4E0D\u505C\u5730\u53D8\u5316\uFF0C\u5982\u679C\u4F7F\u7528 Canvas2D \u7ED8\u5236\uFF0C\u6027\u80FD\u662F\u4F1A\u8FBE\u5230\u74F6\u9888\u7684\u3002\u8FD9\u4E2A\u65F6\u5019\uFF0C\u6211\u4EEC\u5C31\u9700\u8981\u4F7F\u7528 GPU \u80FD\u529B\uFF0C\u76F4\u63A5\u7528 WebGL \u6765\u7ED8\u5236\u3002 \u200B- \u7B2C\u4E8C\u79CD\u60C5\u51B5\uFF0C\u5982\u679C\u6211\u4EEC\u8981\u5BF9\u8F83\u5927\u56FE\u50CF\u7684\u7EC6\u8282\u505A\u50CF\u7D20\u5904\u7406\uFF0C\u6BD4\u5982\uFF0C\u5B9E\u73B0\u7269\u4F53\u7684\u5149\u5F71\u3001\u6D41\u4F53\u6548\u679C\u548C\u4E00\u4E9B\u590D\u6742\u7684\u50CF\u7D20\u6EE4\u955C\u3002\u7531\u4E8E\u8FD9\u4E9B\u6548\u679C\u5F80\u5F80\u8981\u7CBE\u51C6\u5730\u6539\u53D8\u4E00\u4E2A\u56FE\u50CF\u5168\u5C40\u6216\u5C40\u90E8\u533A\u57DF\u7684\u6240\u6709\u50CF\u7D20\u70B9\uFF0C\u8981\u8BA1\u7B97\u7684\u50CF\u7D20\u70B9\u6570\u91CF\u975E\u5E38\u7684\u591A\uFF08\u4E00\u822C\u662F\u6570\u5341\u4E07\u751A\u81F3\u4E0A\u767E\u4E07\u6570\u91CF\u7EA7\u7684\uFF09\uFF0C\u6211\u4EEC\u4E5F\u8981\u7528 WebGL \u6765\u7ED8\u5236\u3002 \u200B- \u7B2C\u4E09\u79CD\u60C5\u51B5\u662F\u7ED8\u5236 3D \u7269\u4F53\u3002\u56E0\u4E3A WebGL \u5185\u7F6E\u4E86\u5BF9 3D \u7269\u4F53\u7684\u6295\u5F71\u3001\u6DF1\u5EA6\u68C0\u6D4B\u7B49\u7279\u6027\uFF0C\u6240\u4EE5\u7528\u5B83\u6765\u6E32\u67D3 3D \u7269\u4F53\u5C31\u4E0D\u9700\u8981\u6211\u4EEC\u81EA\u5DF1\u5BF9\u5750\u6807\u505A\u5E95\u5C42\u7684\u5904\u7406\u4E86\u3002\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0CWebGL \u65E0\u8BBA\u662F\u5728\u4F7F\u7528\u4E0A\u8FD8\u662F\u6027\u80FD\u4E0A\u90FD\u6709\u5F88\u5927\u4F18\u52BF\u3002</p><p>\u8981\u4F7F\u7528 WebGL \u7ED8\u56FE\uFF0C\u6211\u4EEC\u5FC5\u987B\u8981\u6DF1\u5165\u7EC6\u8282\u3002\u6362\u53E5\u8BDD\u8BF4\u5C31\u662F\uFF0C\u6211\u4EEC\u5FC5\u987B\u8981\u548C\u5185\u5B58\u3001GPU \u6253\u4EA4\u9053\uFF0C\u771F\u6B63\u63A7\u5236\u56FE\u5F62\u8F93\u51FA\u7684\u6BCF\u4E00\u4E2A\u7EC6\u8282\u3002\u6570\u636E\u7ECF\u8FC7CPU\uFF08\u4E2D\u592E\u5904\u7406\u5355\u5143\uFF0C\u8D1F\u8D23\u903B\u8F91\u8BA1\u7B97\uFF09\u5904\u7406\uFF0C\u6210\u4E3A\u5177\u6709\u7279\u5B9A\u7ED3\u6784\u7684\u51E0\u4F55\u4FE1\u606F\u3002\u7136\u540E\uFF0C\u4FE1\u606F\u4F1A\u88AB\u9001\u5230GPU\uFF08\u56FE\u5F62\u5904\u7406\u5355\u5143\uFF0C\u8D1F\u8D23\u56FE\u5F62\u8BA1\u7B97\uFF09\u4E2D\u8FDB\u884C\u5904\u7406\u3002\u5728GPU\u4E2D\u8981\u7ECF\u8FC7\u4E24\u4E2A\u6B65\u9AA4\u751F\u6210\u5149\u6805\u4FE1\u606F\uFF08\u6784\u6210\u56FE\u50CF\u7684\u50CF\u7D20\u77E9\u9635\uFF09\uFF0C\u8FD9\u4E9B\u5149\u6805\u4FE1\u606F\u4F1A\u8F93\u51FA\u5230\u5E27\u7F13\u5B58\uFF08\u4E00\u5757\u5185\u5B58\u5730\u5740\uFF09\u4E2D\uFF0C\u6700\u540E\u6E32\u67D3\u5230\u5C4F\u5E55\u4E0A\u3002</p><p>GPU \u662F\u7531\u5927\u91CF\u7684\u5C0F\u578B\u5904\u7406\u5355\u5143\u6784\u6210\u7684\uFF0C\u5B83\u53EF\u80FD\u8FDC\u8FDC\u6CA1\u6709 CPU \u90A3\u4E48\u5F3A\u5927\uFF0C\u4F46\u80DC\u5728\u6570\u91CF\u4F17\u591A\uFF0C\u53EF\u4EE5\u4FDD\u8BC1\u6BCF\u4E2A\u5355\u5143\u5904\u7406\u4E00\u4E2A\u7B80\u5355\u7684\u4EFB\u52A1\u3002\u5373\u4F7F\u6211\u4EEC\u8981\u5904\u7406\u4E00\u5F20 800 * 600 \u5927\u5C0F\u7684\u56FE\u7247\uFF0CGPU \u4E5F\u53EF\u4EE5\u4FDD\u8BC1\u8FD9 48 \u4E07\u4E2A\u50CF\u7D20\u70B9\u5206\u522B\u5BF9\u5E94\u4E00\u4E2A\u5C0F\u5355\u5143\uFF0C\u8FD9\u6837\u6211\u4EEC\u5C31\u53EF\u4EE5\u540C\u65F6\u5BF9\u6BCF\u4E2A\u50CF\u7D20\u70B9\u8FDB\u884C\u8BA1\u7B97\u4E86\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u5728\u524D\u7AEF\u6574\u4F53\u8FC5\u731B\u53D1\u5C55\u7684\u5927\u73AF\u5883\u4E0B\uFF0C\u56E0\u4E3A\u4EA7\u54C1\u9700\u6C42\u7684\u9A71\u52A8\uFF0C\u56FE\u5F62\u5B66\u65B9\u5411\u6280\u672F\u53D1\u5C55\u4E5F\u975E\u5E38\u8FC5\u901F\uFF0C\u524D\u7AEF\u56FE\u5F62\u5B66\u5C5E\u4E8E\u4E00\u4E2A\u6BD4\u8F83\u5C0F\u4F17\u7684\u9886\u57DF\uFF0C\u751A\u81F3\u5EF6\u4F38\u5230\u53EF\u89C6\u5316\u65B9\u5411\u4F9D\u7136\u5C5E\u4E8E\u4E00\u6BD4\u8F83\u5C0F\u4F17\u7684\u9886\u57DF\uFF0C\u90A3\u4E48\u771F\u6B63\u963B\u6321\u7684\u6280\u672F\u95E8\u69DB\u662F\u4EC0\u4E48\u5462\uFF1F</p><p>\u5176\u5B9E\u5C31\u662F\u5BF9\u4F7F\u7528\u8005\u7684\u6570\u5B66\u57FA\u7840\u6BD4\u8F83\u9AD8\uFF0C\u4F46\u5F53\u6211\u4EEC\u771F\u6B63\u7A81\u7834\u4E86\u8FD9\u4E2A\u6280\u672F\u95E8\u69DB\uFF0C\u751A\u81F3\u53EF\u89C6\u5316\u65B9\u5411\u4E0A\u7A81\u7834\u4E86WebGL\u8FD9\u79CD\u66F4\u504F\u5E95\u5C42\u7684\u95E8\u69DB\uFF0C\u5728\u884C\u4E1A\u91CC\u6211\u4EEC\u4F1A\u662F\u7279\u522B\u975E\u5E38\u6709\u7ADE\u4E89\u529B\u7684\u3002</p><ul><li>\u56FE\u5F62\u57FA\u7840\uFF1A\u5E26\u4F60\u719F\u6089 HTML/CSS\u3001SVG\u3001Canvas2D \u548C WebGL \u8FD9\u56DB\u79CD\u56FE\u5F62\u7CFB\u7EDF\uFF0C\u5B66\u4F1A\u5B83\u4EEC\u7684\u57FA\u672C\u7528\u6CD5\u3001\u4F18\u70B9\u548C\u5C40\u9650\u6027\uFF0C\u4ECE\u800C\u80FD\u5728\u5B9E\u9645\u5E94\u7528\u4E2D\u9009\u62E9\u5408\u9002\u7684\u56FE\u5F62\u7CFB\u7EDF\uFF0C\u4EE5\u8FBE\u5230\u6700\u597D\u7684\u89C6\u89C9\u6548\u679C\u3002</li><li>\u6570\u5B66\u57FA\u7840\uFF1A\u6DF1\u5165\u8BB2\u89E3\u5411\u91CF\u3001\u77E9\u9635\u8FD0\u7B97\u3001\u53C2\u6570\u65B9\u7A0B\u3001\u4E09\u89D2\u5256\u5206\u4EE5\u53CA\u4EFF\u5C04\u53D8\u6362\u7B49\u5185\u5BB9\uFF0C\u5E76\u914D\u5408\u7EFC\u5408\u8FD0\u7528\uFF0C\u5E2E\u4F60\u5EFA\u7ACB\u4E00\u5957\u901A\u7528\u7684\u6570\u5B66\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u9002\u7528\u4E8E\u6240\u6709\u56FE\u5F62\u7CFB\u7EDF\uFF0C\u4EE5\u6B64\u6765\u89E3\u51B3\u53EF\u89C6\u5316\u56FE\u5F62\u5448\u73B0\u4E2D\u7684\u5927\u90E8\u5206\u95EE\u9898\u3002</li><li>\u89C6\u89C9\u5448\u73B0\uFF1A\u548C\u4F60\u8BA8\u8BBA\u50CF\u7D20\u5316\u3001\u52A8\u753B\u30013D \u548C\u4EA4\u4E92\u7B49\u8BDD\u9898\uFF0C\u7ED3\u5408\u7F8E\u989C\u3001\u56FE\u7247\u5904\u7406\u548C\u89C6\u89C9\u7279\u6548\u7B49\u5B9E\u9645\u4F8B\u5B50\uFF0C\u6765\u5E94\u7528\u5404\u79CD\u6570\u5B66\u548C\u56FE\u5F62\u5B66\u77E5\u8BC6\uFF0C\u5E2E\u4F60\u5168\u9762\u63D0\u5347\u89C6\u89C9\u5448\u73B0\u7684\u80FD\u529B\uFF0C\u5B9E\u73B0\u66F4\u9AD8\u7EA7\u7684\u89C6\u89C9\u6548\u679C\u3002</li><li>\u6027\u80FD\u4F18\u5316\uFF1A\u901A\u8FC7\u5B66\u4E60 WebGL \u6E32\u67D3\u590D\u6742\u7684 2D\u30013D \u6A21\u578B\u7684\u65B9\u6CD5\uFF0C\u4E86\u89E3\u53EF\u89C6\u5316\u9AD8\u6027\u80FD\u6E32\u67D3\u7684\u6280\u672F\u601D\u8DEF\u3002\u5728\u8FD9\u4E00\u6A21\u5757\u6708\u5F71\u5C06\u548C\u4F60\u5206\u4EAB\u4ED6\u603B\u7ED3\u7684\u4E00\u4E9B\u6210\u719F\u7684\u65B9\u6CD5\u8BBA\uFF0C\u5E2E\u52A9\u4F60\u5728\u5B9E\u73B0\u53EF\u89C6\u5316\u9879\u76EE\u7684\u65F6\u5019\uFF0C\u89E3\u51B3\u5927\u89C4\u6A21\u6570\u636E\u6279\u91CF\u6E32\u67D3\u7684\u6027\u80FD\u74F6\u9888\u95EE\u9898\u3002</li><li>\u6570\u636E\u9A71\u52A8\uFF1A\u7ED3\u5408 3D \u67F1\u72B6\u56FE\u30013D \u5C42\u7EA7\u7ED3\u6784\u56FE\u30013D \u97F3\u4E50\u53EF\u89C6\u5316\u7B49\u6848\u4F8B\uFF0C\u8BB2\u89E3\u6570\u636E\u5904\u7406\u7684\u6280\u5DE7\uFF0C\u771F\u6B63\u6B63\u5C06\u6570\u636E\u548C\u89C6\u89C9\u5448\u73B0\u7ED3\u5408\u8D77\u6765\uFF0C\u5B9E\u73B0\u5177\u6709\u79D1\u6280\u611F\u7684 3D \u53EF\u89C6\u5316\u5927\u5C4F\u6548\u679C\uFF0C\u6700\u7EC8\u5F62\u6210\u5B8C\u6574\u7684\u53EF\u89C6\u5316\u89E3\u51B3\u65B9\u6848\u3002</li></ul>',26),s=[i];function n(h,d,p,c,S,o){return t(),a("div",null,s)}var G=e(l,[["render",n]]);export{b as __pageData,G as default};
