import{_ as i,c as l,o as e,a}from"./app.9aa1eb8d.js";const x='{"title":"3_1 Linux \u5165\u95E8","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4EC0\u4E48\u662FLinux\uFF1F","slug":"\u4EC0\u4E48\u662Flinux\uFF1F"},{"level":2,"title":"\u4EC0\u4E48\u662F\u7EC8\u7AEF\uFF1F","slug":"\u4EC0\u4E48\u662F\u7EC8\u7AEF\uFF1F"},{"level":2,"title":"\u5982\u4F55\u6253\u5F00\u7EC8\u7AEF\uFF1F","slug":"\u5982\u4F55\u6253\u5F00\u7EC8\u7AEF\uFF1F"},{"level":2,"title":"\u4EC0\u4E48\u662F\u547D\u4EE4\uFF1F","slug":"\u4EC0\u4E48\u662F\u547D\u4EE4\uFF1F"},{"level":2,"title":"\u4EC0\u4E48\u662F\u8DEF\u5F84\uFF1F","slug":"\u4EC0\u4E48\u662F\u8DEF\u5F84\uFF1F"},{"level":2,"title":"\u5E38\u7528Linux\u547D\u4EE4\uFF1F","slug":"\u5E38\u7528linux\u547D\u4EE4\uFF1F"}],"relativePath":"tech/site/3_1.md","lastUpdated":1655551077723}',n={},t=a('<h1 id="_3-1-linux-\u5165\u95E8" tabindex="-1">3_1 Linux \u5165\u95E8 <a class="header-anchor" href="#_3-1-linux-\u5165\u95E8" aria-hidden="true">#</a></h1><p>\u672C\u8282\u5C06\u5206\u4EABLinux\u7684\u57FA\u7840\u6559\u7A0B\u53CA\u5E38\u7528\u547D\u4EE4\u3002</p><h2 id="\u4EC0\u4E48\u662Flinux\uFF1F" tabindex="-1">\u4EC0\u4E48\u662FLinux\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662Flinux\uFF1F" aria-hidden="true">#</a></h2><p>Linux\u662F\u4E09\u79CD\u4E3B\u6D41\u7684\u64CD\u4F5C\u7CFB\u7EDF\u4E4B\u4E00\uFF0C\u5176\u4ED6\u4E24\u79CD\u662F\u6211\u4EEC\u719F\u77E5\u7684windows\u7CFB\u7EDF\u548CMac\u7CFB\u7EDF\uFF0C\u666E\u901A\u5927\u4F17\u4E00\u822C\u4E0D\u77E5\u9053\uFF0C\u56E0\u4E3ALinux\u66F4\u591A\u7684\u662F\u5E94\u7528\u4E8E\u670D\u52A1\u5668\u4E0A\u3002 \u5B83\u662F\u4E00\u5957\u514D\u8D39\u4F7F\u7528\u548C\u81EA\u7531\u4F20\u64AD\u7684\u7C7BUnix\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u5E76\u4E14\u7EE7\u627F\u4E86Unix\u4EE5\u7F51\u7EDC\u4E3A\u6838\u5FC3\u7684\u8BBE\u8BA1\u601D\u60F3\u3002</p><h2 id="\u4EC0\u4E48\u662F\u7EC8\u7AEF\uFF1F" tabindex="-1">\u4EC0\u4E48\u662F\u7EC8\u7AEF\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u7EC8\u7AEF\uFF1F" aria-hidden="true">#</a></h2><p>\u600E\u6837\u770B\u51FA\u7535\u5F71\u4E2D\u54EA\u4E2A\u4EBA\u662F\u7535\u8111\u5927\u795E\uFF1F\u5C31\u662F\u770B\u4ED6\u662F\u4E0D\u662F\u5728\u7528\u7EC8\u7AEF\u3002\u64CD\u4F5C\u7CFB\u7EDF\u5206\u4E3A\u4E24\u4E2A\u90E8\u5206\uFF1A</p><ul><li>\u5185\u6838\uFF1A\u8D1F\u8D23\u7CFB\u7EDF\u7684\u5168\u90E8\u903B\u8F91\u64CD\u4F5C\uFF0C\u7531\u6D77\u91CF\u547D\u4EE4\u7EC4\u6210\uFF1B</li><li>\u7528\u6237\u4EA4\u4E92\u754C\u9762\uFF1A\u5F00\u673A\u4E4B\u540E\u6240\u6709\u6211\u4EEC\u6240\u770B\u5230\u7684\u4E1C\u897F\uFF0C\u6BD4\u5982\u7A97\u53E3\uFF0C\u8F6F\u4EF6\u7B49</li></ul><p>\u7EC8\u7AEF\u5C31\u662F\u8FDE\u63A5\u5185\u6838\u4E0E\u4EA4\u4E92\u754C\u9762\u7684\u8FD9\u5EA7\u6865\uFF0C\u5B83\u5141\u8BB8\u7528\u6237\u5728\u4EA4\u4E92\u754C\u9762\u4E0A\u6253\u5F00\u4E00\u4E2A\u53EB\u505A\u300CTerminal \u7EC8\u7AEF\u300D\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5728\u5176\u4E2D\u8F93\u5165\u547D\u4EE4\uFF0C\u7CFB\u7EDF\u4F1A\u76F4\u63A5\u7ED9\u51FA\u53CD\u9988\u3002</p><h2 id="\u5982\u4F55\u6253\u5F00\u7EC8\u7AEF\uFF1F" tabindex="-1">\u5982\u4F55\u6253\u5F00\u7EC8\u7AEF\uFF1F <a class="header-anchor" href="#\u5982\u4F55\u6253\u5F00\u7EC8\u7AEF\uFF1F" aria-hidden="true">#</a></h2><ul><li>\u5BF9\u4E8EWindows\uFF0Cwin10\u4EE5\u4E0B\u7248\u672C\uFF0C\u4E0D\u652F\u6301Linux\u98CE\u683C\u7684terminal\uFF0C\u4E00\u822C\u9700\u8981\u5B89\u88C5git bash, \u91CC\u9762\u96C6\u6210\u4E86\u5927\u591A\u6570\u7684Linux\u547D\u4EE4\uFF1B</li><li>\u5BF9\u4E8EMac \u7CFB\u7EDF\uFF0C\u6709\u5982\u4E0B\u4E09\u79CD\u6253\u5F00\u65B9\u5F0F\uFF1A\u9996\u5148\uFF0CF4 \u53BB\u627E \u5230\u5BF9\u5E94\u7684 \u56FE\u6807\uFF0C \u5176\u6B21\uFF0C\u53EF\u4EE5\u5728\u641C\u7D22\u6846 \u8F93\u5165Terminal\uFF0C\u6700\u540E\u53EF\u4EE5\u628A\u628A\u56FA\u5B9A\u5230 \u9762\u677F\uFF0C\u7ECF\u5E38\u4F7F\u7528\u7684\u8BDD \u4F1A \u7279\u522B \u4FBF\u5229\u3002</li></ul><h2 id="\u4EC0\u4E48\u662F\u547D\u4EE4\uFF1F" tabindex="-1">\u4EC0\u4E48\u662F\u547D\u4EE4\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u547D\u4EE4\uFF1F" aria-hidden="true">#</a></h2><p>\u547D\u4EE4\u5C31\u662F \u4F60\u544A\u8BC9\u7535\u8111\u5E0C\u671B\u5B83\u505A\u4EC0\u4E48\u7684 \u90A3\u53E5\u8BDD\uFF0C\u5206\u4E3A\uFF1A</p><ul><li>\u7CFB\u7EDF\u81EA\u5E26\u547D\u4EE4\uFF0C\u5982ls\uFF0Ccd\u7B49\u547D\u4EE4\uFF1B</li><li>\u9700\u8981\u5B89\u88C5\u624D\u80FD\u4F7F\u7528\u7684\u547D\u4EE4\uFF0C\u5982node\u548Cnpm\u7B49\u547D\u4EE4\uFF1B</li></ul><h2 id="\u4EC0\u4E48\u662F\u8DEF\u5F84\uFF1F" tabindex="-1">\u4EC0\u4E48\u662F\u8DEF\u5F84\uFF1F <a class="header-anchor" href="#\u4EC0\u4E48\u662F\u8DEF\u5F84\uFF1F" aria-hidden="true">#</a></h2><p>\u5F53\u4F60\u9700\u8981\u4F7F\u7528\u7EC8\u7AEF\u5BF9\u6587\u4EF6\u5939\u8FDB\u884C\u64CD\u4F5C\u65F6\uFF0C\u7EC8\u7AEF\u9700\u8981\u4F60\u544A\u8BC9\u5B83\uFF0C\u4F60\u60F3\u8981\u4FEE\u6539\u7684\u6587\u4EF6\u5728\u54EA\u91CC\uFF0C\u8FD9\u65F6\u6211\u4EEC\u9700\u8981\u4E86\u89E3\u8DEF\u5F84\u7684\u6982\u5FF5\u3002\u7CFB\u7EDF\u4E2D\u7684\u6BCF\u4E00\u4E2A\u6587\u4EF6\u90FD\u6709\u4E00\u4E2A\u5B58\u653E\u4F4D\u7F6E\uFF0C\u8FD9\u4E00\u5B58\u653E\u4F4D\u7F6E\u5C31\u79F0\u4F5C\u8DEF\u5F84\u3002</p><p>\u7EC8\u7AEF\u542F\u52A8\u540E\uFF0C\u5B83\u7684\u9ED8\u8BA4\u8DEF\u5F84\u5728\u5F53\u524D\u7528\u6237\u6587\u4EF6\u5939\u7684\u6839\u76EE\u5F55\u4E0A\uFF0C\u4E3A\u4E86\u786E\u5B9A\u8FD9\u4E00\u70B9\uFF0C\u4F60\u53EF\u4EE5\u8F93\u5165 pwd \u547D\u4EE4\u6765\u67E5\u770B\u5F53\u524D\u8DEF\u5F84\u3002\u82E5\u4F60\u60F3\u6539\u53D8\u5F53\u524D\u8DEF\u5F84\uFF0C\u5219\u53EF\u4EE5\u8F93\u5165 cd /\u5176\u4ED6\u6587\u4EF6\u5939</p><p>\u4F46\u8DEF\u5F84\u5176\u5B9E\u5206\u4E3A\u4E24\u79CD\uFF1A</p><ul><li>\u76F8\u5BF9\u8DEF\u5F84\uFF1A\u8DEF\u5F84\u7684\u6700\u524D\u9762\u4E3A\u4E00\u4E2A\u70B9(\u4EE3\u8868\u5F53\u524D\u8DEF\u5F84)\u6216\u4E24\u4E2A\u70B9(\u4EE3\u8868\u5F53\u524D\u8DEF\u5F84\u7684\u4E0A\u4E00\u8DEF\u5F84)\uFF0C \u5982&#39;./&#39;, &#39;../static&#39;\u7B49\uFF1B</li><li>\u7EDD\u5BF9\u8DEF\u5F84\uFF1A\u8DEF\u5F84\u7684\u6700\u524D\u9762\u53EA\u4E3A&#39;/&#39;\uFF0C\u5982&#39;/root/static&#39;, &#39;/home/user&#39;;</li></ul><h2 id="\u5E38\u7528linux\u547D\u4EE4\uFF1F" tabindex="-1">\u5E38\u7528Linux\u547D\u4EE4\uFF1F <a class="header-anchor" href="#\u5E38\u7528linux\u547D\u4EE4\uFF1F" aria-hidden="true">#</a></h2><ul><li>ls \u67E5\u770B\u76EE\u5F55\u4E0B\u5305\u542B\u7684\u6587\u4EF6\u6216\u76EE\u5F55\uFF1B</li><li>pwd \u5728\u7EC8\u7AEF\u6253\u5370\u5F53\u524D \u76EE\u5F55\uFF1B</li><li>cd \u5207\u6362\u8DEF\u5F84\uFF1B</li><li>mkdir \u521B\u5EFA\u76EE\u5F55\uFF1B</li><li>touch \u521B\u5EFA\u6587\u4EF6</li><li>rm \u5220\u9664\u6587\u4EF6\u6216\u76EE\u5F55\uFF0C\u5220\u9664\u76EE\u5F55\u65F6\u9700\u8981\u52A0 -rf \u53C2\u6570\uFF1B</li><li>mv \u79FB\u52A8\u6587\u4EF6\u6216\u76EE\u5F55\uFF0C\u5728\u5F53\u524D\u76EE\u5F55\u4E0B\u79FB\u52A8\u65F6\u4E3A \u4FEE\u6539\u540D\u5B57\uFF1B</li><li>ssh \u767B\u5F55\u8FDC\u7A0B\u670D\u52A1\u5668\uFF1B</li><li>npm install \u5B89\u88C5node\u9879\u76EE\u4F9D\u8D56\uFF1B</li><li>git pull \u4ECE\u8FDC\u7A0B\u4ED3\u5E93 \u62C9\u53D6\u6700\u65B0\u4EE3\u7801\uFF1B</li><li>man \u8D85\u7EA7\u547D\u4EE4 \u53BB\u67E5\u770B\u7684\u6709\u5173\u547D\u4EE4\u7684\u4ECB\u7ECD\u3002</li></ul><p>linux\u7CFB\u7EDF\u81EA\u5E26\u548C\u53EF\u5B89\u88C5\u4F7F\u7528\u7684\u7EC8\u7AEF\u547D\u4EE4\u5F88\u591A\u5F88\u591A\uFF0C\u672C\u8282\u6211\u4EEC\u53EA\u4ECB\u7ECD\u4E86\u6700\u5E38\u7528\u7684\u51E0\u4E2A\u547D\u4EE4\uFF0C\u5176\u4ED6\u547D\u4EE4\u7528\u5230\u65F6\u53BB\u67E5\u5373\u53EF\u3002</p><p>\u597D\u4E86\uFF0CLinux\u6709\u5173\u7684\u77E5\u8BC6\u5C31\u8BB2\u89E3\u5230\u8FD9\uFF0C\u4E0B\u8282\u6211\u4EEC\u5C06\u6B63\u5F0F\u5F00\u59CB\u642D\u5EFA\u7F51\u7AD9\uFF0C\u6B22\u8FCE\u7EE7\u7EED\u5B66\u4E60\u3002</p>',22),r=[t];function d(s,h,u,c,p,o){return e(),l("div",null,r)}var m=i(n,[["render",d]]);export{x as __pageData,m as default};
