import{_ as e,c as a,o as s,a as r}from"./app.256e99ba.js";const u='{"title":"4_1 koa \u7B80\u4ECB","description":"","frontmatter":{},"headers":[{"level":2,"title":"nodejs\u662F\u4EC0\u4E48\uFF1F","slug":"nodejs\u662F\u4EC0\u4E48\uFF1F"},{"level":2,"title":"\u4F7F\u7528\u539F\u751Fnodejs\u63D0\u4F9Bweb\u670D\u52A1","slug":"\u4F7F\u7528\u539F\u751Fnodejs\u63D0\u4F9Bweb\u670D\u52A1"},{"level":2,"title":"express \u6846\u67B6","slug":"express-\u6846\u67B6"},{"level":2,"title":"koa 1.x\u7684\u5230\u6765","slug":"koa-1-x\u7684\u5230\u6765"},{"level":2,"title":"\u6700\u65B0\u7684koa 2.x\u7684\u5230\u6765","slug":"\u6700\u65B0\u7684koa-2-x\u7684\u5230\u6765"},{"level":2,"title":"express, koa1\u548Ckoa2\u7684\u533A\u522B","slug":"express-koa1\u548Ckoa2\u7684\u533A\u522B"},{"level":3,"title":"koa vs express","slug":"koa-vs-express"},{"level":3,"title":"koa1 vs koa2","slug":"koa1-vs-koa2"}],"relativePath":"tech/site/4_1.md","lastUpdated":1681349490013}',o={},t=r(`<h1 id="_4-1-koa-\u7B80\u4ECB" tabindex="-1">4_1 koa \u7B80\u4ECB <a class="header-anchor" href="#_4-1-koa-\u7B80\u4ECB" aria-hidden="true">#</a></h1><p>\u672C\u8282\u6211\u4EEC\u5C06\u7B80\u5355\u4ECB\u7ECD\u4E00\u4E0B koa\u3002</p><h2 id="nodejs\u662F\u4EC0\u4E48\uFF1F" tabindex="-1">nodejs\u662F\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#nodejs\u662F\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h2><p>Node.js\u662F\u4E00\u4E2A\u521B\u5EFA\u4E8E 2009\u5E74\u7684Javascript\u8FD0\u884C\u73AF\u5883\u3002\u5B83\u4F7F\u7528\u4E86\u4E00\u4E2A\u4E8B\u4EF6\u9A71\u52A8\u3001\u975E\u963B\u585E\u5F0F I/O \u7684\u6A21\u578B\uFF0C\u57FA\u4E8E\u56DE\u8C03\u5B9E\u73B0\u7684\u5F02\u6B65\u7F16\u7A0B\uFF0C\u4F7F\u5176\u8F7B\u91CF\u53C8\u9AD8\u6548\u3002</p><p>\u4F8B\u5982\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4E00\u8FB9\u8BFB\u53D6\u6587\u4EF6\uFF0C\u4E00\u8FB9\u6267\u884C\u5176\u4ED6\u547D\u4EE4\uFF0C\u5728\u6587\u4EF6\u8BFB\u53D6\u5B8C\u6210\u540E\uFF0C\u6211\u4EEC\u5C06\u6587\u4EF6\u5185\u5BB9\u4F5C\u4E3A\u56DE\u8C03\u51FD\u6570\u7684\u53C2\u6570\u8FD4\u56DE\u3002\u8FD9\u6837\u5728\u6267\u884C\u4EE3\u7801\u65F6\u5C31\u6CA1\u6709\u963B\u585E\u6216\u7B49\u5F85\u6587\u4EF6 I/O \u64CD\u4F5C\u3002</p><p>\u8FD9\u5C31\u5927\u5927\u63D0\u9AD8\u4E86 Node.js \u7684\u6027\u80FD\uFF0C\u53EF\u4EE5\u5904\u7406\u5927\u91CF\u7684\u5E76\u53D1\u8BF7\u6C42\uFF0C\u56E0\u6B64\uFF0CNode \u4E3B\u8981\u7528\u5728\u5F00\u53D1 Web \u5E94\u7528\u3002</p><h2 id="\u4F7F\u7528\u539F\u751Fnodejs\u63D0\u4F9Bweb\u670D\u52A1" tabindex="-1">\u4F7F\u7528\u539F\u751Fnodejs\u63D0\u4F9Bweb\u670D\u52A1 <a class="header-anchor" href="#\u4F7F\u7528\u539F\u751Fnodejs\u63D0\u4F9Bweb\u670D\u52A1" aria-hidden="true">#</a></h2><p>\u8981\u5728nodejs\u4E2D\u63D0\u4F9Bweb\u670D\u52A1\uFF0C\u6211\u4EEC\u9700\u8981\u5F15\u5165 nodejs \u7684Http\u6A21\u5757:require(&#39;http&#39;), \u7136\u540E\u8C03\u7528createServer\u65B9\u6CD5\uFF0C\u4F20\u5165\u4E00\u4E2A\u51FD\u6570\u4F5C\u4E3A\u53C2\u6570\u5373\u53EF\uFF1A</p><div class="language-"><pre><code>const Server = http.createServer((req, res) =&gt; {
  res.writeHead(200,{&#39;Content-Type&#39;: &#39;application/json;charset=utf-8;&#39;});
  res.write(&#39;{text: &quot;\u5199\u4EE3\u7801\u5F88\u5FEB\u4E50\u554A!&quot;}&#39;);
  res.end();
}).listen(8888)\uFF1B
</code></pre></div><p>\u53EF\u4EE5\u770B\u5230\uFF0C\u4EE3\u7801\u53EF\u8BFB\u6027\u4E0D\u662F\u5F88\u597D\uFF0C\u5F00\u53D1\u4E5F\u4E0D\u662F\u5F88\u4FBF\u5229\uFF0C\u4E8E\u662F\u5C31\u6709\u4E86 Express \u8FD9\u4E2A\u7B2C\u4E00\u4EE3\u6700\u6D41\u884C\u7684web\u6846\u67B6\u3002</p><h2 id="express-\u6846\u67B6" tabindex="-1">express \u6846\u67B6 <a class="header-anchor" href="#express-\u6846\u67B6" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u770B\u770B\uFF0C\u4F7F\u7528express\uFF0C \u600E\u4E48\u5B9E\u73B0\u4E00\u4E2A\u6700\u7B80\u5355\u7684\u5165\u95E8\u7F51\u7AD9\uFF1A</p><div class="language-"><pre><code>var express = require(&#39;express&#39;);
var app = express();
app.get(&#39;/&#39;, function (req, res) {
    res.send(&#39;\u5199\u4EE3\u7801\u5F88\u5FEB\u4E50!&#39;);
});
app.listen(8888);
</code></pre></div><p>\u53EF\u4EE5\u770B\u5230\uFF0C\u4EE3\u7801\u7CBE\u7B80\u4E86\u5F88\u591A\uFF0C\u867D\u7136express\u7684API\u5F88\u7B80\u5355\uFF0C\u4F46\u662F\u5B83\u662F\u57FA\u4E8EES5\u7684\u8BED\u6CD5\uFF0C\u8981\u5B9E\u73B0\u5F02\u6B65\u4EE3\u7801\uFF0C\u53EA\u6709\u4E00\u4E2A\u65B9\u6CD5\uFF1A\u56DE\u8C03\u3002\u5982\u679C\u5F02\u6B65\u5D4C\u5957\u5C42\u6B21\u8FC7\u591A\uFF0C\u4EE3\u7801\u5199\u8D77\u6765\u5C31\u975E\u5E38\u96BE\u770B\uFF0C\u6BD4\u5982\u8BFB\u53D6\u4E00\u4E2A\u6587\u4EF6\u6210\u529F\u540E\u518D\u8BFB\u53D6\u4E00\u4E2A\u6587\u4EF6\uFF1A</p><div class="language-"><pre><code>app.get(&#39;/file&#39;, function (req, res) {
    fs.readFile(&#39;/file1&#39;, function (err, data) {
        if (err) { res.status(500).send(&#39;\u8BFB\u53D6\u6587\u4EF61\u5931\u8D25&#39;); }
        fs.readFile(&#39;/file2&#39;, function (err, data) {
            if (err) {  res.status(500).send(&#39;\u8BFB\u53D6\u6587\u4EF62\u5931\u8D25&#39;); }
            res.type(&#39;text/plain&#39;);
            res.send(data);
        });
    });
});
</code></pre></div><p>\u867D\u7136\u53EF\u4EE5\u7528async\u8FD9\u6837\u7684\u5E93\u6765\u7EC4\u7EC7\u5F02\u6B65\u4EE3\u7801\uFF0C\u4F46\u662F\u7528\u56DE\u8C03\u5199\u5F02\u6B65\u5B9E\u5728\u662F\u592A\u75DB\u82E6\u4E86\uFF0C\u7279\u522B\u662F\u5F02\u6B65\u5D4C\u5957\u591A\u7684\u65F6\u5019\uFF0C\u8FD9\u5C31\u662FJS\u8FDC\u53E4\u65F6\u671F\u7684 \u56DE\u8C03\u5730\u72F1\uFF01</p><h2 id="koa-1-x\u7684\u5230\u6765" tabindex="-1">koa 1.x\u7684\u5230\u6765 <a class="header-anchor" href="#koa-1-x\u7684\u5230\u6765" aria-hidden="true">#</a></h2><p>\u968F\u7740ES6\u5728\u65B0\u7248Node.js\u83B7\u5F97\u652F\u6301ES6\uFF0Cexpress\u7684\u56E2\u961F\u57FA\u4E8EES6\u7684generator\u63A8\u51FA\u4E86koa\u8FD9\u4E2A web \u6846\u67B6\u3002\u548Cexpress\u76F8\u6BD4\uFF0Ckoa 1.0\u4F7F\u7528generator\u5B9E\u73B0\u5F02\u6B65\uFF0C\u6BD4\u5982\u5B9E\u73B0\u4E0A\u9762\u540C\u6837\u7684\u529F\u80FD\uFF0C\u4EE3\u7801\u5982\u4E0B\uFF1A</p><div class="language-"><pre><code>var koa = require(&#39;koa&#39;);
var app = koa();

app.use(&#39;/file&#39;, function *() {
    yield readFile1();
    var data = yield readFile2();
    this.body = data;
});
app.listen(8888);
</code></pre></div><p>\u53EF\u4EE5\u770B\u5230\uFF0C\u7528generator\u5B9E\u73B0\u5F02\u6B65\u6BD4\u56DE\u8C03\u7B80\u5355\u4E86\u4E0D\u5C11\uFF0C\u4F46\u662Fgenerator\u7684\u672C\u610F\u5E76\u4E0D\u662F\u5F02\u6B65\u3002Promise\u624D\u662F\u4E3A\u5F02\u6B65\u8BBE\u8BA1\u7684\uFF0C\u4F46\u662FPromise\u7684\u5199\u6CD5\u5F88\u590D\u6742\u3002\u4E3A\u4E86\u7B80\u5316\u5F02\u6B65\u4EE3\u7801\uFF0CES7\u5F15\u5165\u4E86\u65B0\u7684\u5173\u952E\u5B57async\u548Cawait\uFF0C\u53EF\u4EE5\u8F7B\u677E\u5730\u628A\u4E00\u4E2Afunction\u53D8\u4E3A\u5F02\u6B65\u6A21\u5F0F\uFF1A</p><div class="language-"><pre><code>async function () {
    var data = await fs.read(&#39;/file1&#39;);
}
</code></pre></div><h2 id="\u6700\u65B0\u7684koa-2-x\u7684\u5230\u6765" tabindex="-1">\u6700\u65B0\u7684koa 2.x\u7684\u5230\u6765 <a class="header-anchor" href="#\u6700\u65B0\u7684koa-2-x\u7684\u5230\u6765" aria-hidden="true">#</a></h2><p>koa\u56E2\u961F\u5E76\u6CA1\u6709\u6B62\u6B65\u4E8Ekoa 1.0\uFF0C\u4ED6\u4EEC\u975E\u5E38\u8D85\u524D\u5730\u57FA\u4E8EES7\u5F00\u53D1\u4E86koa2\uFF0C\u548Ckoa 1\u76F8\u6BD4\uFF0Ckoa2\u5B8C\u5168\u4F7F\u7528Promise\u5E76\u914D\u5408async\u6765\u5B9E\u73B0\u5F02\u6B65\u3002</p><div class="language-"><pre><code>app.use(async (ctx, next) =&gt; {
    await next();
    var data = await readFile();
    ctx.response.type = &#39;text/plain&#39;;
    ctx.response.body = data;
});
</code></pre></div><p>\u901A\u8FC7\u4EE5\u4E0A\u5206\u4EAB\uFF0C\u5927\u5BB6\u53EF\u4EE5\u770B\u5230\uFF0C\u6211\u4EEC\u9879\u76EE\u4F7F\u7528koa2\uFF0C\u662F\u6309\u7167node-&gt;express-&gt;koa1-&lt;koa2 \u4E00\u6B65\u6B65\u968F\u7740\u65B0\u7684JS\u89C4\u8303\u901A\u8FC7\u5E76\u83B7\u5F97\u652F\u6301\u7684\u53D1\u5C55\u800C\u6765\u7684\uFF0C\u51FA\u73B0\u7684\u4E3B\u8981\u539F\u56E0\u5C31\u662F\u4E3A\u4E86\u5728node\u5F00\u53D1\u4E2D\u4F18\u96C5\u7684\u5199\u5F02\u6B65\u4EE3\u7801\u3002</p><h2 id="express-koa1\u548Ckoa2\u7684\u533A\u522B" tabindex="-1">express, koa1\u548Ckoa2\u7684\u533A\u522B <a class="header-anchor" href="#express-koa1\u548Ckoa2\u7684\u533A\u522B" aria-hidden="true">#</a></h2><h3 id="koa-vs-express" tabindex="-1">koa vs express <a class="header-anchor" href="#koa-vs-express" aria-hidden="true">#</a></h3><ul><li><p>\u76F8\u540C\u70B9</p><ul><li>\u6784\u5EFA web \u5E94\u7528\u7684 node \u6846\u67B6</li><li>\u603B\u4F53\u6765\u770B\u8BED\u6CD5\u5DEE\u522B\u4E0D\u5927\uFF0C\u6BD4\u5982\u90FD\u662F\u5F15\u5165\u540E\u6846\u67B6\u540E\u5B9E\u4F8B\u540E\uFF0C\u4E2D\u95F4\u4EF6\u5904\u7406\u4EE5\u53CA\u76D1\u542C\u7AEF\u53E3\uFF1B</li><li>\u90FD\u662F\u540C\u4E00\u4E2A\u56E2\u961F\u5F00\u53D1\u7EF4\u62A4\u7684</li></ul></li><li><p>\u533A\u522B</p><ul><li>express: \u6846\u67B6\u5185\u5BB9\u66F4\u4E30\u5BCC\uFF0C\u6709\u66F4\u5927\u7684\u793E\u533A\uFF0C\u5386\u53F2\u66F4\u60A0\u4E45\uFF0C\u6587\u6863\u66F4\u4E30\u5BCC\uFF0C\u7528\u6237\u7FA4\u66F4\u5927\uFF0C\u652F\u6301 jade \u7B49\u524D\u7AEF\u6A21\u677F\u8BED\u8A00\uFF0Cexpress\u6CA1\u6709\u7EDF\u4E00\u7684\u9519\u8BEF\u5904\u7406\uFF0C\u800Ckoa\u6709\u9ED8\u8BA4\u7684\u9519\u8BEF\u5904\u7406\u65B9\u5F0F\u3002</li><li>koa: \u66F4\u5C0F\u3001\u66F4\u5BCC\u6709\u8868\u73B0\u529B\u3001\u66F4\u5065\u58EE\uFF0C<em><strong>Koa \u652F\u6301 es6 \u8BED\u6CD5</strong></em>\uFF0CKoa \u5728\u5185\u6838\u65B9\u6CD5\u4E2D\u4E0D\u7ED1\u5B9A\u4EFB\u4F55\u4E2D\u95F4\u4EF6\uFF0C\u4E0D\u63D0\u4F9B\u8DEF\u7531\u529F\u80FD\u548C\u67D0\u4E9B\u5DE5\u5177\uFF0C\u6452\u5F03\u4E86\u7684\u56DE\u8C03\uFF0C\u91C7\u7528 generator \u6216 promise\u7684\u65B9\u5F0F\uFF0C\u5728 Context\u4E2D\uFF0CKoa \u5BF9 request \u548C response \u8FDB\u884C\u4E86\u5C01\u88C5\uFF0C\u4F7F\u7528\u65B9\u5F0F\u4E5F\u76F8\u5E94\u6539\u53D8\uFF0C\u5982\u8FD4\u56DE\u7B80\u5355\u5185\u5BB9\uFF0C\u5728koa: this.body = \u2018hello world\u2019\uFF0C\u800C\u5728express: res.send(\u2018hello world\u2019)\u3002</li></ul></li></ul><h3 id="koa1-vs-koa2" tabindex="-1">koa1 vs koa2 <a class="header-anchor" href="#koa1-vs-koa2" aria-hidden="true">#</a></h3><ul><li>\u4E2D\u95F4\u4EF6\u7684\u4F7F\u7528: \xA0koa1\u4F9D\u8D56 co \u5E76\u91C7\u7528 generator \u51FD\u6570\uFF0C\u5728\u51FD\u6570\u5185\u4F7F\u7528 yield \u8BED\u53E5\uFF0C\u800Ckoa2\u589E\u52A0\u4E86\u7BAD\u5934\u51FD\u6570\uFF0C\u79FB\u9664\u4E86 co \u4F9D\u8D56\uFF0C\u4F7F\u7528 Promise\uFF0C\u56E0\u6B64\u53EF\u4EE5\u7ED3\u5408 async\uFF0Cawait \u4F7F\u7528\uFF1B</li><li>context \u5BF9\u8C61\u7684\u83B7\u53D6\uFF1Akoa1\u4E3Athis \u5BF9\u8C61\uFF0Cthis.req, this.res\uFF1Bkoa2: cxt \u53C2\u6570\uFF0Ccxt.req, cxt.res\u3002</li></ul><p>\u4E86\u89E3\u4E86koa\u7684\u80CC\u666F\u77E5\u8BC6\u540E\uFF0C\u4E0B\u4E00\u8282\u6211\u4EEC\u5C06\u7B80\u5355\u5206\u6790\u5176\u6E90\u7801\u5566\uFF01</p>`,31),n=[t];function i(d,p,l,c,h,k){return s(),a("div",null,n)}var v=e(o,[["render",i]]);export{u as __pageData,v as default};
